 1→import { useQuery } from '@tanstack/react-query';
 2→import { useEffect, useState } from 'react';
 3→
 4→import { useAuthStore } from '../stores/authStore';
 5→import type { TerminologySystem } from '../components/TerminologyBadge';
 6→
 7→const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:8000';
 8→
 9→export interface TerminologySearchResult {
10→    code: string;
11→    display: string;
12→    system: string;
13→    semantic_type?: string;
14→    confidence: number;
15→}
