17→export function useTerminologySearch(
18→    system: TerminologySystem,
19→    query: string
20→): {
21→    results: TerminologySearchResult[];
22→    isLoading: boolean;
23→    isError: boolean;
24→} {
25→    // Debounce query by 300ms
26→    const [debouncedQuery, setDebouncedQuery] = useState(query);
27→
28→    useEffect(() => {
29→        const timeout = setTimeout(() => {
30→            setDebouncedQuery(query);
31→        }, 150);
32→
33→        return () => {
34→            clearTimeout(timeout);
35→        };
36→    }, [query]);
37→
38→    // Only enable when debouncedQuery has at least 3 characters
39→    const enabled = debouncedQuery.trim().length >= 3;
40→
41→    const { data, isFetching, isError } = useQuery({
42→        queryKey: ['terminology-search', system, debouncedQuery],
43→        queryFn: async ({ signal }) => {
44→            const token = useAuthStore.getState().token;
45→            const headers: HeadersInit = {
46→                'Content-Type': 'application/json',
47→                ...(token ? { Authorization: `Bearer ${token}` } : {}),
48→            };
49→
50→            const response = await fetch(
51→                `${API_BASE_URL}/api/terminology/${system}/search?q=${encodeURIComponent(debouncedQuery)}`,
52→                {
53→                    headers,
54→                    signal,
55→                }
56→            );
57→
58→            if (response.status === 401) {
59→                useAuthStore.getState().logout();
60→                throw new Error('Session expired');
61→            }
62→
63→            if (!response.ok) {
64→                throw new Error(`API Error: ${response.status} ${response.statusText}`);
65→            }
66→
67→            return response.json() as Promise<TerminologySearchResult[]>;
68→        },
69→        enabled,
70→        staleTime: 5 * 60 * 1000, // 5 minutes
71→        gcTime: 10 * 60 * 1000, // 10 minutes
72→        retry: 1,
73→        refetchOnWindowFocus: false,
74→    });
75→
76→    return {
77→        results: data ?? [],
78→        isLoading: isFetching,
79→        isError,
80→    };
81→}
