106→            // Step 1: Get signed upload URL from backend
107→            const uploadResp = await fetchApi<UploadResponse>('/protocols/upload', {
108→                method: 'POST',
109→                body: JSON.stringify({
110→                    filename: file.name,
111→                    content_type: file.type,
112→                    file_size_bytes: file.size,
113→                }),
114→            });
115→
116→            // Step 2: Upload file directly to GCS via signed URL
117→            const putResp = await fetch(uploadResp.upload_url, {
118→                method: 'PUT',
119→                headers: { 'Content-Type': 'application/pdf' },
120→                body: file,
