127→            // Step 3: Read file as base64 for quality scoring, then confirm
128→            const arrayBuffer = await file.arrayBuffer();
129→            const bytes = new Uint8Array(arrayBuffer);
130→            let binary = '';
131→            for (let i = 0; i < bytes.length; i++) {
132→                binary += String.fromCharCode(bytes[i]);
133→            }
134→            const pdfBase64 = btoa(binary);
135→
136→            await fetchApi(`/protocols/${uploadResp.protocol_id}/confirm-upload`, {
137→                method: 'POST',
138→                body: JSON.stringify({ pdf_bytes_base64: pdfBase64 }),
