 21→import { cn } from '../lib/utils';
 22→
 23→function formatTimestamp(dateStr: string): string {
 24→    return new Date(dateStr).toLocaleString();
 25→}
 26→
 27→export default function ReviewPage() {
 28→    const { batchId } = useParams<{ batchId: string }>();
 29→    const [auditOpen, setAuditOpen] = useState(false);
 30→    const [activeTab, setActiveTab] = useState('criteria');
 31→    const [activeCriterion, setActiveCriterion] = useState<Criterion | null>(null);
 32→
 33→    // Filter state
 34→    const [searchText, setSearchText] = useState('');
 35→    const [statusFilter, setStatusFilter] = useState<string>('all');
 36→    const [typeFilter, setTypeFilter] = useState<string>('all');
 37→    const [confidenceFilter, setConfidenceFilter] = useState<string>('all');
 38→    const [debouncedSearch] = useDebounce(searchText, 300);
 39→
 40→    // Fetch batch criteria
 41→    const {
 42→        data: criteria,
 43→        isLoading: criteriaLoading,
 44→        error: criteriaError,
 45→    } = useBatchCriteria(batchId ?? '', 'confidence', 'asc');
 46→
 47→    // Fetch batch info (find batch from list)
 48→    const { data: batchListData } = useBatchList(1, 100);
 49→    const batch = batchListData?.items.find((b) => b.id === batchId);
 50→
 51→    // Fetch PDF URL using protocol_id from batch
 52→    const { data: pdfData, isLoading: pdfLoading } = usePdfUrl(batch?.protocol_id ?? '');
 53→
 54→    // Review action mutation
 55→    const reviewAction = useReviewAction();
 56→
 57→    // Entity data
 58→    const { data: entities } = useEntityListByBatch(batchId ?? '');
 59→    const entityAction = useEntityAction();
 60→
 61→    // Audit log
 62→    const { data: auditData } = useAuditLog(1, 20, 'criteria');
 63→
 64→    function handleAction(criterionId: string, action: ReviewActionRequest) {
 65→        reviewAction.mutate({ criteriaId: criterionId, ...action });
 66→    }
 67→
 68→    function handleEntityAction(entityId: string, action: EntityActionRequest) {
 69→        entityAction.mutate({ entityId, ...action });
 70→    }
 71→
 72→    function handleCriterionClick(criterion: Criterion) {
 73→        // Toggle: clicking the same criterion deselects it
 74→        if (activeCriterion?.id === criterion.id) {
 75→            setActiveCriterion(null);
 76→        } else {
 77→            setActiveCriterion(criterion);
 78→        }
 79→    }
 80→
 81→    const reviewedCount = criteria?.filter((c) => c.review_status !== null).length ?? 0;
 82→    const totalCount = criteria?.length ?? 0;
 83→    const progressPercent = totalCount > 0 ? Math.round((reviewedCount / totalCount) * 100) : 0;
 84→
 85→    // Group entities by criteria_id
 86→    const groupedEntities: Record<string, EntityResponse[]> = {};
 87→    if (entities) {
 88→        for (const entity of entities) {
 89→            if (!groupedEntities[entity.criteria_id]) {
 90→                groupedEntities[entity.criteria_id] = [];
 91→            }
 92→            groupedEntities[entity.criteria_id]?.push(entity);
 93→        }
 94→    }
 95→
 96→    // Client-side filtering
 97→    const filteredCriteria = useMemo(() => {
 98→        if (!criteria) return [];
 99→        return criteria.filter((c) => {
100→            // Text search (case-insensitive)
101→            if (debouncedSearch && !c.text.toLowerCase().includes(debouncedSearch.toLowerCase())) {
102→                return false;
103→            }
104→            // Status filter
105→            if (statusFilter !== 'all') {
106→                const isReviewed = c.review_status !== null;
107→                if (statusFilter === 'reviewed' && !isReviewed) return false;
108→                if (statusFilter === 'pending' && isReviewed) return false;
109→            }
110→            // Type filter
111→            if (typeFilter !== 'all' && c.criteria_type !== typeFilter) {
112→                return false;
113→            }
114→            // Confidence filter (high>=0.85, medium>=0.7, low<0.7)
115→            if (confidenceFilter !== 'all') {
116→                if (confidenceFilter === 'high' && c.confidence < 0.85) return false;
117→                if (confidenceFilter === 'medium' && (c.confidence < 0.7 || c.confidence >= 0.85))
118→                    return false;
119→                if (confidenceFilter === 'low' && c.confidence >= 0.7) return false;
120→            }
121→            return true;
122→        });
123→    }, [criteria, debouncedSearch, statusFilter, typeFilter, confidenceFilter]);
124→
125→    // Section grouping with pending-first sort
126→    const { uncategorizedCriteria, inclusionCriteria, exclusionCriteria } = useMemo(() => {
127→        const uncategorized: Criterion[] = [];
128→        const inclusion: Criterion[] = [];
129→        const exclusion: Criterion[] = [];
130→
131→        for (const c of filteredCriteria) {
132→            if (
133→                !c.criteria_type ||
134→                (c.criteria_type !== 'inclusion' && c.criteria_type !== 'exclusion')
135→            ) {
136→                uncategorized.push(c);
137→            } else if (c.criteria_type === 'inclusion') {
138→                inclusion.push(c);
139→            } else {
140→                exclusion.push(c);
141→            }
142→        }
143→
144→        const sortFn = (a: Criterion, b: Criterion) => {
145→            const aPending = a.review_status === null;
146→            const bPending = b.review_status === null;
147→            if (aPending && !bPending) return -1;
148→            if (!aPending && bPending) return 1;
149→            return 0;
150→        };
151→        uncategorized.sort(sortFn);
152→        inclusion.sort(sortFn);
153→        exclusion.sort(sortFn);
154→
155→        return {
156→            uncategorizedCriteria: uncategorized,
157→            inclusionCriteria: inclusion,
158→            exclusionCriteria: exclusion,
159→        };
160→    }, [filteredCriteria]);
161→
162→    const countReviewed = (list: Criterion[]) =>
163→        list.filter((c) => c.review_status !== null).length;
164→
165→    if (criteriaLoading) {
166→        return (
167→            <div className="flex items-center justify-center h-screen">
168→                <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
169→            </div>
170→        );
171→    }
172→
173→    if (criteriaError) {
174→        return (
175→            <div className="container mx-auto p-6">
176→                <Link
177→                    to="/reviews"
178→                    className="inline-flex items-center text-sm text-muted-foreground hover:text-foreground mb-6"
179→                >
180→                    <ArrowLeft className="h-4 w-4 mr-1" />
181→                    Back to Review Queue
182→                </Link>
183→                <div className="rounded-lg border border-destructive/50 bg-destructive/10 p-6 text-center">
184→                    <p className="text-destructive">
185→                        Failed to load criteria: {criteriaError.message}
186→                    </p>
187→                </div>
188→            </div>
189→        );
190→    }
191→
192→    return (
193→        <div className="h-screen flex flex-col">
194→            <PanelGroup direction="horizontal" className="flex-1">
195→                {/* Left panel: PDF Viewer */}
196→                <Panel defaultSize={50} minSize={30}>
197→                    <div className="h-full flex flex-col">
198→                        <div className="px-4 py-2 border-b bg-card">
199→                            <h2 className="text-sm font-medium text-muted-foreground">
200→                                Protocol PDF
201→                            </h2>
202→                        </div>
203→                        <div className="flex-1 overflow-hidden">
204→                            {pdfLoading ? (
205→                                <div className="flex items-center justify-center h-full">
206→                                    <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
207→                                </div>
208→                            ) : pdfData?.url ? (
209→                                <PdfViewer
210→                                    url={pdfData.url}
211→                                    targetPage={activeCriterion?.page_number ?? null}
212→                                    highlightText={activeCriterion?.text ?? null}
213→                                />
214→                            ) : (
215→                                <div className="flex items-center justify-center h-full">
216→                                    <p className="text-muted-foreground">No PDF available</p>
217→                                </div>
218→                            )}
219→                        </div>
220→                    </div>
221→                </Panel>
222→
223→                <PanelResizeHandle className="w-1 bg-border hover:bg-primary/20 transition-colors" />
224→
225→                {/* Right panel: Criteria and Entities Review */}
226→                <Panel defaultSize={50} minSize={30}>
227→                    <div className="h-full overflow-auto">
228→                        {/* Header */}
229→                        <div className="sticky top-0 z-10 bg-card border-b px-4 py-3 space-y-3">
230→                            <div className="flex items-center justify-between">
231→                                <Link
232→                                    to="/reviews"
233→                                    className="inline-flex items-center text-sm text-muted-foreground hover:text-foreground"
234→                                >
235→                                    <ArrowLeft className="h-4 w-4 mr-1" />
236→                                    Review Queue
237→                                </Link>
238→                                {batch && (
239→                                    <span
240→                                        className={cn(
241→                                            'inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-medium',
242→                                            batch.status === 'approved'
243→                                                ? 'bg-green-100 text-green-800'
244→                                                : batch.status === 'rejected'
245→                                                  ? 'bg-red-100 text-red-800'
246→                                                  : batch.status === 'in_progress'
247→                                                    ? 'bg-blue-100 text-blue-800'
248→                                                    : 'bg-yellow-100 text-yellow-800'
249→                                        )}
250→                                    >
251→                                        {batch.status.replace(/_/g, ' ')}
252→                                    </span>
253→                                )}
254→                            </div>
255→
256→                            {/* Progress bar */}
257→                            <div>
258→                                <div className="flex items-center justify-between mb-1">
259→                                    <span className="text-sm text-muted-foreground">
260→                                        {reviewedCount}/{totalCount} criteria reviewed
261→                                    </span>
262→                                    <span className="text-sm font-medium">{progressPercent}%</span>
263→                                </div>
264→                                <div className="h-2 w-full rounded-full bg-muted overflow-hidden">
265→                                    <div
266→                                        className={cn(
267→                                            'h-full rounded-full transition-all',
268→                                            progressPercent === 100 ? 'bg-green-500' : 'bg-blue-500'
269→                                        )}
270→                                        style={{ width: `${progressPercent}%` }}
271→                                    />
272→                                </div>
273→                            </div>
274→                        </div>
275→
276→                        {/* Tabs for Criteria and Entities */}
277→                        <Tabs.Root value={activeTab} onValueChange={setActiveTab}>
278→                            <Tabs.List className="flex border-b bg-card px-4">
279→                                <Tabs.Trigger
280→                                    value="criteria"
281→                                    className={cn(
282→                                        'px-4 py-2 text-sm font-medium border-b-2 transition-colors',
283→                                        activeTab === 'criteria'
284→                                            ? 'border-primary text-foreground'
285→                                            : 'border-transparent text-muted-foreground hover:text-foreground'
286→                                    )}
287→                                >
288→                                    Criteria
289→                                </Tabs.Trigger>
290→                                <Tabs.Trigger
291→                                    value="entities"
292→                                    className={cn(
