354→                                            <h2 className="text-lg font-bold mb-3 text-foreground">
355→                                                To Be Sorted ({countReviewed(uncategorizedCriteria)}
356→                                                /{uncategorizedCriteria.length} reviewed)
357→                                            </h2>
358→                                            <div className="space-y-4">
359→                                                {uncategorizedCriteria.map((c) => (
360→                                                    <CriterionCard
361→                                                        key={c.id}
362→                                                        criterion={c}
363→                                                        onAction={handleAction}
364→                                                        isSubmitting={reviewAction.isPending}
365→                                                        onCriterionClick={handleCriterionClick}
366→                                                        isActive={activeCriterion?.id === c.id}
367→                                                    />
368→                                                ))}
369→                                            </div>
370→                                        </section>
371→                                    )}
372→
373→                                    {/* Inclusion Criteria section */}
374→                                    {inclusionCriteria.length > 0 && (
375→                                        <section>
376→                                            <h2 className="text-lg font-bold mb-3 text-foreground">
377→                                                Inclusion Criteria (
378→                                                {countReviewed(inclusionCriteria)}/
379→                                                {inclusionCriteria.length} reviewed)
380→                                            </h2>
381→                                            <div className="space-y-4">
382→                                                {inclusionCriteria.map((c) => (
383→                                                    <CriterionCard
384→                                                        key={c.id}
385→                                                        criterion={c}
386→                                                        onAction={handleAction}
387→                                                        isSubmitting={reviewAction.isPending}
388→                                                        onCriterionClick={handleCriterionClick}
389→                                                        isActive={activeCriterion?.id === c.id}
390→                                                    />
391→                                                ))}
392→                                            </div>
393→                                        </section>
394→                                    )}
395→
396→                                    {/* Exclusion Criteria section */}
397→                                    {exclusionCriteria.length > 0 && (
398→                                        <section>
399→                                            <h2 className="text-lg font-bold mb-3 text-foreground">
400→                                                Exclusion Criteria (
401→                                                {countReviewed(exclusionCriteria)}/
402→                                                {exclusionCriteria.length} reviewed)
403→                                            </h2>
404→                                            <div className="space-y-4">
405→                                                {exclusionCriteria.map((c) => (
406→                                                    <CriterionCard
407→                                                        key={c.id}
408→                                                        criterion={c}
409→                                                        onAction={handleAction}
410→                                                        isSubmitting={reviewAction.isPending}
411→                                                        onCriterionClick={handleCriterionClick}
412→                                                        isActive={activeCriterion?.id === c.id}
413→                                                    />
414→                                                ))}
415→                                            </div>
416→                                        </section>
417→                                    )}
418→
419→                                    {/* Empty state when filters match nothing */}
420→                                    {filteredCriteria.length === 0 &&
421→                                        criteria &&
422→                                        criteria.length > 0 && (
423→                                            <div className="text-center py-8">
424→                                                <p className="text-muted-foreground">
425→                                                    No criteria match your filters
426→                                                </p>
427→                                            </div>
428→                                        )}
429→
430→                                    {/* Original empty state when no criteria at all */}
431→                                    {(!criteria || criteria.length === 0) && (
432→                                        <div className="text-center py-8">
433→                                            <p className="text-muted-foreground">
434→                                                No criteria found for this batch
435→                                            </p>
436→                                        </div>
437→                                    )}
438→
439→                                    {/* Audit trail section */}
440→                                    <div className="mt-6 border-t pt-4">
441→                                        <button
442→                                            type="button"
443→                                            className="flex items-center gap-2 text-sm font-medium text-muted-foreground hover:text-foreground transition-colors"
444→                                            onClick={() => setAuditOpen(!auditOpen)}
445→                                        >
446→                                            {auditOpen ? (
447→                                                <ChevronUp className="h-4 w-4" />
448→                                            ) : (
449→                                                <ChevronDown className="h-4 w-4" />
450→                                            )}
451→                                            Audit Trail
452→                                        </button>
453→
454→                                        {auditOpen && auditData && (
455→                                            <div className="mt-3 space-y-2">
456→                                                {auditData.items.length === 0 ? (
457→                                                    <p className="text-sm text-muted-foreground">
458→                                                        No audit entries yet
459→                                                    </p>
460→                                                ) : (
461→                                                    auditData.items.map((entry) => (
462→                                                        <div
463→                                                            key={entry.id}
464→                                                            className="flex items-start gap-3 rounded-md border bg-muted/30 px-3 py-2 text-xs"
465→                                                        >
466→                                                            <span className="text-muted-foreground whitespace-nowrap">
467→                                                                {formatTimestamp(entry.created_at)}
468→                                                            </span>
469→                                                            <span className="font-medium">
470→                                                                {entry.event_type}
471→                                                            </span>
472→                                                            {entry.actor_id && (
473→                                                                <span className="text-muted-foreground">
474→                                                                    by {entry.actor_id}
475→                                                                </span>
476→                                                            )}
477→                                                            {entry.target_type &&
478→                                                                entry.target_id && (
479→                                                                    <span className="text-muted-foreground">
480→                                                                        on {entry.target_type}:
481→                                                                        {entry.target_id.slice(
482→                                                                            0,
483→                                                                            8
484→                                                                        )}
485→                                                                    </span>
486→                                                                )}
487→                                                        </div>
488→                                                    ))
489→                                                )}
490→                                            </div>
491→                                        )}
492→                                    </div>
493→                                </div>
