  1→import * as Dialog from '@radix-ui/react-dialog';
  2→import { Controller, useForm } from 'react-hook-form';
  3→
  4→import { Button } from './ui/Button';
  5→
  6→const REJECT_REASONS = [
  7→    { value: 'not_criteria', label: 'Not a criteria' },
  8→    { value: 'incorrect_grounding', label: 'Incorrect entity grounding' },
  9→    { value: 'poor_splitting', label: 'Poor splitting into composites' },
 10→    { value: 'duplicate', label: 'Duplicate of another criterion' },
 11→    { value: 'other', label: 'Other' },
 12→];
 13→
 14→interface RejectDialogFormValues {
 15→    reasons: string[];
 16→    comment: string;
 17→}
 18→
 19→interface RejectDialogProps {
 20→    open: boolean;
 21→    onOpenChange: (open: boolean) => void;
 22→    onConfirm: (data: { reasons: string[]; comment?: string }) => void;
 23→}
 24→
 25→export default function RejectDialog({ open, onOpenChange, onConfirm }: RejectDialogProps) {
 26→    const { control, handleSubmit, reset } = useForm<RejectDialogFormValues>({
 27→        defaultValues: {
 28→            reasons: [],
 29→            comment: '',
 30→        },
 31→    });
 32→
 33→    function handleCancel() {
 34→        reset();
 35→        onOpenChange(false);
 36→    }
 37→
 38→    function onSubmit(data: RejectDialogFormValues) {
 39→        onConfirm({
 40→            reasons: data.reasons,
 41→            comment: data.comment || undefined,
 42→        });
 43→        reset();
 44→        onOpenChange(false);
 45→    }
 46→
 47→    return (
 48→        <Dialog.Root open={open} onOpenChange={onOpenChange}>
 49→            <Dialog.Portal>
 50→                <Dialog.Overlay className="fixed inset-0 bg-black/50 z-50" />
 51→                <Dialog.Content className="fixed top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 bg-white dark:bg-card rounded-lg p-6 max-w-md w-full z-50 shadow-lg">
 52→                    <Dialog.Title className="text-base font-semibold mb-4">
 53→                        Reject Criterion
 54→                    </Dialog.Title>
 55→
 56→                    <form onSubmit={handleSubmit(onSubmit)}>
 57→                        <div className="mb-4">
 58→                            <p className="text-sm text-muted-foreground mb-3">
 59→                                Select one or more reasons for rejection:
 60→                            </p>
 61→                            <Controller
 62→                                control={control}
 63→                                name="reasons"
 64→                                render={({ field }) => (
 65→                                    <div className="space-y-2">
 66→                                        {REJECT_REASONS.map((reason) => (
 67→                                            <label
 68→                                                key={reason.value}
 69→                                                className="flex items-center gap-2 cursor-pointer"
 70→                                            >
 71→                                                <input
 72→                                                    type="checkbox"
 73→                                                    value={reason.value}
 74→                                                    checked={field.value.includes(reason.value)}
 75→                                                    onChange={(e) => {
 76→                                                        if (e.target.checked) {
 77→                                                            field.onChange([
 78→                                                                ...field.value,
 79→                                                                reason.value,
 80→                                                            ]);
 81→                                                        } else {
 82→                                                            field.onChange(
 83→                                                                field.value.filter(
 84→                                                                    (v) => v !== reason.value
 85→                                                                )
 86→                                                            );
 87→                                                        }
 88→                                                    }}
 89→                                                    className="h-4 w-4 rounded border-gray-300 text-red-600 focus:ring-red-500"
 90→                                                />
 91→                                                <span className="text-sm">{reason.label}</span>
 92→                                            </label>
 93→                                        ))}
 94→                                    </div>
 95→                                )}
 96→                            />
 97→                        </div>
 98→
 99→                        <div className="mb-5">
100→                            <label
101→                                htmlFor="reject-comment"
102→                                className="block text-sm font-medium text-muted-foreground mb-1"
103→                            >
104→                                Additional comment{' '}
105→                                <span className="text-xs text-gray-400">(optional)</span>
106→                            </label>
107→                            <Controller
108→                                control={control}
109→                                name="comment"
110→                                render={({ field }) => (
111→                                    <textarea
112→                                        id="reject-comment"
