import * as Popover from '@radix-ui/react-popover';
import { Command } from 'cmdk';
import { Loader2 } from 'lucide-react';
import { useState } from 'react';

import type { TerminologySearchResult } from '../hooks/useTerminologySearch';
import { useTerminologySearch } from '../hooks/useTerminologySearch';
import { cn } from '../lib/utils';
import type { TerminologySystem } from './TerminologyBadge';

const SYSTEM_LABELS: Record<TerminologySystem, string> = {
    rxnorm: 'RxNorm',
    icd10: 'ICD-10',
    snomed: 'SNOMED',
    loinc: 'LOINC',
    hpo: 'HPO',
    umls: 'UMLS',
};

interface TerminologyComboboxProps {
    system: TerminologySystem;
    value: string;
    onSelect: (result: TerminologySearchResult) => void;
    onChange: (value: string) => void;
    placeholder?: string;
    className?: string;
}

export function TerminologyCombobox({
    system,
    value,
    onSelect,
    onChange,
    placeholder,
    className,
}: TerminologyComboboxProps) {
    const [open, setOpen] = useState(false);
    const [inputValue, setInputValue] = useState(value);

    const { results, isLoading, isCircuitOpen, validationHint } = useTerminologySearch(
        system,
        inputValue
    );

    const systemLabel = SYSTEM_LABELS[system];
    const resolvedPlaceholder = placeholder ?? `Search ${systemLabel}...`;

    // Show popover when there's input and either results, loading, or enough chars
    const shouldShowPopover =
        inputValue.trim().length > 0 &&
        (results.length > 0 || isLoading || inputValue.trim().length >= 3);

    function handleInputChange(newValue: string) {
        setInputValue(newValue);
        onChange(newValue);
        setOpen(true);
    }

    function handleSelect(result: TerminologySearchResult) {
        setInputValue(result.display);
        onSelect(result);
        setOpen(false);
    }

    return (
        <div>
            <Popover.Root open={open && shouldShowPopover} onOpenChange={setOpen}>
                <Popover.Trigger asChild>
                    <input
                        type="text"
                        value={inputValue}
                        onChange={(e) => handleInputChange(e.target.value)}
                        onFocus={() => setOpen(true)}
                        placeholder={resolvedPlaceholder}
                        className={cn(
                            'w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background',
                            'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring',
                            isCircuitOpen && 'border-amber-400',
                            className
                        )}
                    />
                </Popover.Trigger>
                <Popover.Portal>
                    <Popover.Content
                        className="z-50 w-[var(--radix-popover-trigger-width)] rounded-md border bg-popover p-0 shadow-md outline-none"
                        align="start"
                        sideOffset={4}
                    >
                        <Command shouldFilter={false} className="rounded-md">
                            <Command.List className="max-h-[300px] overflow-y-auto">
                                {isLoading && (
                                    <Command.Loading>
                                        <div className="flex items-center gap-2 px-4 py-3 text-sm text-muted-foreground">
                                            <Loader2 className="h-4 w-4 animate-spin" />
                                            <span>Searching {systemLabel}...</span>
                                        </div>
                                    </Command.Loading>
                                )}

                                {!isLoading &&
                                    inputValue.trim().length > 0 &&
                                    inputValue.trim().length < 3 && (
                                        <Command.Empty>
                                            <div className="px-4 py-3 text-sm text-muted-foreground">
                                                Type at least 3 characters
                                            </div>
                                        </Command.Empty>
                                    )}

                                {!isLoading &&
                                    inputValue.trim().length >= 3 &&
                                    results.length === 0 && (
                                        <Command.Empty>
                                            <div className="px-4 py-3 text-sm text-muted-foreground">
                                                No results found in {systemLabel}
                                            </div>
                                        </Command.Empty>
                                    )}

                                {results.map((result) => (
                                    <Command.Item
                                        key={`${result.system}-${result.code}`}
                                        value={result.code}
                                        onSelect={() => handleSelect(result)}
                                        className={cn(
                                            'flex flex-col gap-1 px-4 py-3 cursor-pointer',
                                            'hover:bg-accent hover:text-accent-foreground',
                                            'aria-selected:bg-accent aria-selected:text-accent-foreground',
                                            'border-b border-border last:border-b-0'
                                        )}
                                    >
                                        <div className="flex items-center justify-between w-full">
                                            <span className="font-medium text-sm">
                                                {result.display}
                                            </span>
                                            <span className="text-xs text-muted-foreground ml-2">
                                                {result.code}
                                            </span>
                                        </div>
                                        {result.semantic_type && (
                                            <div className="text-xs text-muted-foreground">
                                                {result.semantic_type}
                                            </div>
                                        )}
                                    </Command.Item>
                                ))}
                            </Command.List>
                        </Command>
                    </Popover.Content>
                </Popover.Portal>
            </Popover.Root>

            {validationHint && <p className="mt-1 text-xs text-amber-600">{validationHint}</p>}
        </div>
    );
}

import {
    CheckCircle,
    ChevronDown,
    ChevronUp,
    ExternalLink,
    Loader2,
    Pencil,
    XCircle,
} from 'lucide-react';
import { useState } from 'react';

import type { EntityActionRequest, EntityResponse } from '../hooks/useEntities';
import type { TerminologySearchResult } from '../hooks/useTerminologySearch';
import { cn } from '../lib/utils';
import { ErrorBadge, TerminologyBadge } from './TerminologyBadge';
import { TerminologyCombobox } from './TerminologyCombobox';
import { Button } from './ui/Button';

interface EntityCardProps {
    entity: EntityResponse;
    onAction: (entityId: string, action: EntityActionRequest) => void;
    isSubmitting: boolean;
}

function EntityTypeBadge({ type }: { type: string }) {
    const typeConfig: Record<string, { label: string; colorClass: string }> = {
        Condition: { label: 'Condition', colorClass: 'bg-blue-100 text-blue-800' },
        Medication: { label: 'Medication', colorClass: 'bg-purple-100 text-purple-800' },
        Procedure: { label: 'Procedure', colorClass: 'bg-green-100 text-green-800' },
        Lab_Value: { label: 'Lab Value', colorClass: 'bg-orange-100 text-orange-800' },
        Demographic: { label: 'Demographic', colorClass: 'bg-gray-100 text-gray-800' },
        Biomarker: { label: 'Biomarker', colorClass: 'bg-teal-100 text-teal-800' },
    };

    const config = typeConfig[type] || { label: type, colorClass: 'bg-gray-100 text-gray-800' };

    return (
        <span
            className={cn(
                'inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-medium',
                config.colorClass
            )}
        >
            {config.label}
        </span>
    );
}

function GroundingConfidenceBadge({ confidence }: { confidence: number | null }) {
    if (confidence === null) {
        return (
            <span className="inline-flex items-center rounded-full bg-gray-100 text-gray-800 px-2.5 py-0.5 text-xs font-medium">
                No confidence
            </span>
        );
    }

    const percentage = Math.round(confidence * 100);

    let label: string;
    let colorClass: string;

    if (confidence >= 0.85) {
        label = 'High';
        colorClass = 'bg-green-100 text-green-800';
    } else if (confidence >= 0.7) {
        label = 'Medium';
        colorClass = 'bg-yellow-100 text-yellow-800';
    } else {
        label = 'Low';
        colorClass = 'bg-red-100 text-red-800';
    }

    return (
        <span
            className={cn(
                'inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-medium',
                colorClass
            )}
            title={`Grounding confidence: ${percentage}%`}
        >
            {label} ({percentage}%)
        </span>
    );
}

function ReviewStatusBadge({ status }: { status: string | null }) {
    const statusConfig: Record<string, { label: string; colorClass: string }> = {
        approved: { label: 'Approved', colorClass: 'bg-green-100 text-green-800' },
        rejected: { label: 'Rejected', colorClass: 'bg-red-100 text-red-800' },
        modified: { label: 'Modified', colorClass: 'bg-yellow-100 text-yellow-800' },
    };

    const config = status ? statusConfig[status] : null;
    const label = config?.label ?? 'Pending';
    const colorClass = config?.colorClass ?? 'bg-gray-100 text-gray-800';

    return (
        <span
            className={cn(
                'inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-medium',
                colorClass
            )}
        >
            {label}
        </span>
    );
}

/** Determine which systems are relevant for a given entity type. */
function getRelevantSystems(entityType: string): string[] {
    switch (entityType) {
        case 'Medication':
            return ['rxnorm', 'umls'];
        case 'Condition':
            return ['icd10', 'snomed', 'umls'];
        case 'Lab_Value':
            return ['loinc', 'umls'];
        case 'Biomarker':
            return ['hpo', 'umls'];
        default:
            return ['snomed', 'umls'];
    }
}

export default function EntityCard({ entity, onAction, isSubmitting }: EntityCardProps) {
    const [isEditing, setIsEditing] = useState(false);
    const [contextOpen, setContextOpen] = useState(false);

    // Edit state for all code fields
    const [editCui, setEditCui] = useState(entity.umls_cui ?? '');
    const [editSnomed, setEditSnomed] = useState(entity.snomed_code ?? '');
    const [editPreferredTerm, setEditPreferredTerm] = useState(entity.preferred_term ?? '');
    const [editRxnorm, setEditRxnorm] = useState(entity.rxnorm_code ?? '');
    const [editIcd10, setEditIcd10] = useState(entity.icd10_code ?? '');
    const [editLoinc, setEditLoinc] = useState(entity.loinc_code ?? '');
    const [editHpo, setEditHpo] = useState(entity.hpo_code ?? '');

    const isLowConfidence = (entity.grounding_confidence ?? 0) < 0.7;
    const relevantSystems = getRelevantSystems(entity.entity_type);

    // Check if entity has any codes
    const hasCodes =
        entity.umls_cui ||
        entity.snomed_code ||
        entity.rxnorm_code ||
        entity.icd10_code ||
        entity.loinc_code ||
