 25→export function EntityTypeBadge({ type }: { type: string }) {
 26→    const typeConfig: Record<string, { label: string; colorClass: string }> = {
 27→        Condition: { label: 'Condition', colorClass: 'bg-blue-100 text-blue-800' },
 28→        Medication: { label: 'Medication', colorClass: 'bg-purple-100 text-purple-800' },
 29→        Procedure: { label: 'Procedure', colorClass: 'bg-green-100 text-green-800' },
 30→        Lab_Value: { label: 'Lab Value', colorClass: 'bg-orange-100 text-orange-800' },
 31→        Demographic: { label: 'Demographic', colorClass: 'bg-gray-100 text-gray-800' },
 32→        Biomarker: { label: 'Biomarker', colorClass: 'bg-teal-100 text-teal-800' },
 33→    };
 34→
 35→    const config = typeConfig[type] || { label: type, colorClass: 'bg-gray-100 text-gray-800' };
 36→
 37→    return (
 38→        <span
 39→            className={cn(
 40→                'inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-medium',
 41→                config.colorClass
 42→            )}
 43→        >
 44→            {config.label}
 45→        </span>
 46→    );
 47→}
 48→
 49→export function GroundingConfidenceBadge({ confidence }: { confidence: number | null }) {
 50→    if (confidence === null) {
 51→        return (
 52→            <span className="inline-flex items-center rounded-full bg-gray-100 text-gray-800 px-2.5 py-0.5 text-xs font-medium">
 53→                No confidence
 54→            </span>
 55→        );
 56→    }
 57→
 58→    const percentage = Math.round(confidence * 100);
 59→
 60→    let label: string;
 61→    let colorClass: string;
 62→
 63→    if (confidence >= 0.85) {
 64→        label = 'High';
 65→        colorClass = 'bg-green-100 text-green-800';
 66→    } else if (confidence >= 0.7) {
 67→        label = 'Medium';
 68→        colorClass = 'bg-yellow-100 text-yellow-800';
 69→    } else {
 70→        label = 'Low';
 71→        colorClass = 'bg-red-100 text-red-800';
 72→    }
 73→
 74→    return (
 75→        <span
 76→            className={cn(
 77→                'inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-medium',
 78→                colorClass
 79→            )}
 80→            title={`Grounding confidence: ${percentage}%`}
 81→        >
 82→            {label} ({percentage}%)
 83→        </span>
 84→    );
 85→}
 86→
 87→export function ReviewStatusBadge({ status }: { status: string | null }) {
 88→    const statusConfig: Record<string, { label: string; colorClass: string }> = {
 89→        approved: { label: 'Approved', colorClass: 'bg-green-100 text-green-800' },
 90→        rejected: { label: 'Rejected', colorClass: 'bg-red-100 text-red-800' },
 91→        modified: { label: 'Modified', colorClass: 'bg-yellow-100 text-yellow-800' },
 92→    };
 93→
 94→    const config = status ? statusConfig[status] : null;
 95→    const label = config?.label ?? 'Pending';
 96→    const colorClass = config?.colorClass ?? 'bg-gray-100 text-gray-800';
 97→
 98→    return (
 99→        <span
100→            className={cn(
101→                'inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-medium',
102→                colorClass
103→            )}
104→        >
105→            {label}
106→        </span>
107→    );
108→}
