                )}
            </div>

            {/* Entity text */}
            <p className="text-sm font-medium text-foreground mb-3">{entity.text}</p>

            {/* Multi-code badge section */}
            <div className="flex flex-wrap gap-1.5 mb-3">
                {entity.rxnorm_code && (
                    <TerminologyBadge system="rxnorm" code={entity.rxnorm_code} />
