106→export function formatTemporalConstraint(tc: Record<string, unknown>): string {
107→    const duration = 'duration' in tc ? (tc.duration as string) : null;
108→    const relation = 'relation' in tc ? (tc.relation as string) : null;
109→    const referencePoint = 'reference_point' in tc ? (tc.reference_point as string) : null;
110→
111→    if (!duration) return '';
112→
113→    const relationMap: Record<string, string> = {
114→        within: 'Within',
115→        before: 'Before',
116→        after: 'After',
117→        at_least: 'At least',
118→    };
119→
120→    const relationText = relation ? relationMap[relation] || relation : '';
121→    const parts = [relationText, duration];
122→    if (referencePoint) parts.push('of', referencePoint);
123→
124→    return parts.filter(Boolean).join(' ');
125→}
126→
127→export function formatNumericThreshold(threshold: Record<string, unknown>): string {
128→    const value = 'value' in threshold ? threshold.value : null;
129→    const unit = 'unit' in threshold ? (threshold.unit as string) : '';
130→    const comparator = 'comparator' in threshold ? (threshold.comparator as string) : '';
131→    const upperValue = 'upper_value' in threshold ? threshold.upper_value : null;
132→
133→    if (value === null) return '';
134→
135→    if (comparator === 'range' && upperValue !== null) {
136→        return `${value}-${upperValue} ${unit}`.trim();
137→    }
138→
139→    return `${comparator}${value} ${unit}`.trim();
140→}
141→
142→export function extractThresholdsList(nt: Record<string, unknown> | null): Array<Record<string, unknown>> {
143→    if (!nt) return [];
144→
145→    // Shape 1: {"thresholds": [...]} wrapper object
146→    if ('thresholds' in nt && Array.isArray(nt.thresholds)) {
147→        return nt.thresholds as Array<Record<string, unknown>>;
148→    }
149→
150→    // Shape 2: raw array stored directly
151→    if (Array.isArray(nt)) {
152→        return nt as Array<Record<string, unknown>>;
153→    }
154→
155→    // Shape 3: single threshold object without wrapper
156→    if ('value' in nt && 'comparator' in nt) {
157→        return [nt];
158→    }
159→
160→    return [];
161→}
162→
163→/** Map extraction comparator strings to RelationOperator. */
164→function mapComparator(comparator: string): RelationOperator | '' {
165→    const map: Record<string, RelationOperator> = {
166→        '>=': '>=',
167→        '<=': '<=',
168→        '>': '>',
169→        '<': '<',
170→        '==': '=',
171→        '=': '=',
172→        range: 'within',
173→    };
174→    return map[comparator] ?? '';
175→}
176→
177→/** Parse a duration string like "6 months" into {value, unit}. */
178→function parseDuration(duration: string): { value: string; unit: TemporalUnit } {
179→    const match = duration.match(/^(\d+)\s*(days?|weeks?|months?|years?)$/i);
180→    if (match) {
181→        const raw = match[2].toLowerCase().replace(/s$/, '');
182→        const unitMap: Record<string, TemporalUnit> = {
183→            day: 'days',
184→            week: 'weeks',
185→            month: 'months',
186→            year: 'years',
187→        };
188→        return { value: match[1], unit: unitMap[raw] ?? 'days' };
189→    }
190→    return { value: duration, unit: 'days' };
191→}
192→
193→/**
194→ * Build initial form values for the structured editor from a criterion's
195→ * AI-extracted data (numeric_thresholds, temporal_constraint, conditions).
196→ */
197→export function buildInitialValues(criterion: Criterion): StructuredFieldFormValues {
198→    // Priority 1: existing field_mappings from a previous structured edit
199→    const cond = criterion.conditions as Record<string, unknown> | null;
200→    if (cond && 'field_mappings' in cond && Array.isArray(cond.field_mappings)) {
201→        const fms = cond.field_mappings as Array<Record<string, unknown>>;
202→        const mappings: FieldMapping[] = fms.map((fm) => {
203→            const rel = (fm.relation as string) ?? '';
204→            const rawVal = fm.value as Record<string, unknown> | undefined;
205→            let value: FieldValue = { type: 'standard', value: '', unit: '' };
206→            if (rawVal && rawVal.type === 'range') {
207→                value = {
208→                    type: 'range',
209→                    min: String(rawVal.min ?? ''),
210→                    max: String(rawVal.max ?? ''),
211→                    unit: String(rawVal.unit ?? ''),
212→                };
213→            } else if (rawVal && rawVal.type === 'temporal') {
214→                value = {
215→                    type: 'temporal',
216→                    duration: String(rawVal.duration ?? ''),
217→                    unit: (rawVal.unit as TemporalUnit) ?? 'days',
218→                };
219→            } else if (rawVal && rawVal.type === 'standard') {
220→                value = {
221→                    type: 'standard',
222→                    value: String(rawVal.value ?? ''),
223→                    unit: String(rawVal.unit ?? ''),
224→                };
225→            }
226→            return {
227→                entity: String(fm.entity ?? ''),
228→                relation: (rel as RelationOperator) || '',
229→                value,
230→            };
231→        });
232→        if (mappings.length > 0) return { mappings };
233→    }
234→
235→    // Priority 2: infer from AI-extracted data (entities + thresholds + temporal)
236→    const mappings: FieldMapping[] = [];
237→
238→    // Grounded entities provide the "what" (entity name + UMLS/SNOMED IDs)
239→    // Thresholds provide the "how" (comparator + value + unit)
240→    // Match them: Lab_Value/Biomarker entities → numeric thresholds,
241→    // Demographic entities → age-related thresholds, etc.
242→    const entities = criterion.entities ?? [];
243→    const thresholds = extractThresholdsList(criterion.numeric_thresholds);
244→
245→    // Build entity label: prefer UMLS preferred_term, fallback to extracted text
246→    const entityLabel = (e: { preferred_term: string | null; text: string }) =>
247→        e.preferred_term || e.text;
248→
249→    // Simple matching: pair thresholds with relevant entities by type
250→    const measurableEntities = entities.filter(
251→        (e) =>
252→            e.entity_type === 'Lab_Value' ||
253→            e.entity_type === 'Biomarker' ||
254→            e.entity_type === 'Demographic'
255→    );
256→
257→    for (let i = 0; i < thresholds.length; i++) {
258→        const t = thresholds[i];
259→        const comparator = (t.comparator as string) ?? '';
260→        const val = t.value as number | null;
261→        const unit = (t.unit as string) ?? '';
262→        const upperVal = t.upper_value as number | null;
263→
264→        if (val === null || val === undefined) continue;
265→
266→        // Use matching entity if available (positional pairing)
267→        const matchedEntity = measurableEntities[i];
268→        const entity = matchedEntity ? entityLabel(matchedEntity) : '';
269→
270→        if (comparator === 'range' && upperVal != null) {
271→            mappings.push({
272→                entity,
273→                relation: 'within' as RelationOperator,
274→                value: { type: 'range', min: String(val), max: String(upperVal), unit },
275→            });
276→        } else {
277→            const relation = mapComparator(comparator);
278→            mappings.push({
279→                entity,
280→                relation,
281→                value: { type: 'standard', value: String(val), unit },
282→            });
283→        }
284→    }
285→
286→    // Entities without a matching threshold get their own mapping (entity only)
287→    const unmatchedEntities = entities.filter(
288→        (e) =>
289→            e.entity_type === 'Condition' ||
290→            e.entity_type === 'Medication' ||
291→            e.entity_type === 'Procedure'
292→    );
293→    for (const e of unmatchedEntities) {
294→        mappings.push({
295→            entity: entityLabel(e),
296→            relation: '',
297→            value: { type: 'standard', value: '', unit: '' },
298→        });
299→    }
300→
301→    // From temporal constraint
302→    if (criterion.temporal_constraint) {
303→        const tc = criterion.temporal_constraint;
304→        const duration = tc.duration as string | undefined;
305→        const referencePoint = tc.reference_point as string | undefined;
306→
307→        if (duration) {
308→            const parsed = parseDuration(duration);
309→            mappings.push({
310→                entity: referencePoint ?? '',
311→                relation: 'not_in_last' as RelationOperator,
312→                value: { type: 'temporal', duration: parsed.value, unit: parsed.unit },
313→            });
314→        }
315→    }
316→
317→    if (mappings.length === 0) return DEFAULT_FIELD_VALUES;
318→    return { mappings };
319→}
