321→type EditMode = 'none' | 'text' | 'structured';
322→
323→export default function CriterionCard({
324→    criterion,
325→    onAction,
326→    isSubmitting,
327→    onCriterionClick,
328→    isActive,
329→}: CriterionCardProps) {
330→    const [editMode, setEditMode] = useState<EditMode>('none');
331→    const [editText, setEditText] = useState(criterion.text);
332→    const [editType, setEditType] = useState(criterion.criteria_type);
333→    const [editCategory, setEditCategory] = useState(criterion.category ?? '');
334→    const [rationale, setRationale] = useState('');
335→    const [rejectDialogOpen, setRejectDialogOpen] = useState(false);
336→
337→    // Sync local edit state when criterion prop changes (after mutation response)
338→    useEffect(() => {
339→        setEditText(criterion.text);
340→        setEditType(criterion.criteria_type);
341→        setEditCategory(criterion.category ?? '');
342→    }, [criterion.text, criterion.criteria_type, criterion.category]);
343→
344→    function handleApprove() {
345→        onAction(criterion.id, {
346→            action: 'approve',
347→            reviewer_id: 'current-user',
348→        });
349→    }
350→
351→    function handleReject() {
352→        setRejectDialogOpen(true);
353→    }
354→
355→    function handleRejectConfirm(data: { reasons: string[]; comment?: string }) {
356→        onAction(criterion.id, {
357→            action: 'reject',
358→            reviewer_id: 'current-user',
359→            reject_reasons: data.reasons,
360→            comment: data.comment,
361→        });
362→    }
363→
364→    function handleModifySave() {
365→        onAction(criterion.id, {
366→            action: 'modify',
367→            reviewer_id: 'current-user',
368→            modified_text: editText,
369→            modified_type: editType,
370→            modified_category: editCategory || undefined,
371→            comment: rationale || undefined,
372→        });
373→        setRationale('');
374→        setEditMode('none');
375→    }
376→
377→    function handleModifyCancel() {
378→        setEditText(criterion.text);
379→        setEditType(criterion.criteria_type);
380→        setEditCategory(criterion.category ?? '');
381→        setRationale('');
382→        setEditMode('none');
383→    }
384→
385→    function handleStructuredSave(values: {
386→        mappings: Array<{ entity: string; relation: string; value: unknown }>;
387→    }) {
388→        // Convert StructuredFieldFormValues to the modified_structured_fields payload
389→        // Extract the mappings array and send as field_mappings
390→        onAction(criterion.id, {
391→            action: 'modify',
392→            reviewer_id: 'current-user',
393→            modified_structured_fields: {
394→                field_mappings: values.mappings,
395→            },
396→        });
397→        setEditMode('none');
398→    }
399→
400→    return (
401→        <div
402→            className={cn(
403→                'rounded-lg border bg-card p-4 shadow-sm border-l-4',
404→                criterion.review_status === 'approved' && 'border-l-green-500',
405→                criterion.review_status === 'rejected' && 'border-l-red-500',
406→                criterion.review_status === 'modified' && 'border-l-blue-500',
407→                !criterion.review_status && 'border-l-yellow-400'
408→            )}
409→        >
410→            {/* Header row */}
411→            <div className="flex flex-wrap items-center gap-2 mb-3">
412→                <CriteriaTypeBadge type={criterion.criteria_type} />
413→                {criterion.category && (
414→                    <span className="inline-flex items-center rounded-full bg-gray-100 px-2.5 py-0.5 text-xs font-medium text-gray-700">
415→                        {criterion.category}
416→                    </span>
417→                )}
418→                <ConfidenceBadge confidence={criterion.confidence} />
419→                <CriterionReviewStatusBadge status={criterion.review_status} />
420→                {criterion.page_number != null && (
421→                    <span className="inline-flex items-center rounded-full bg-slate-100 px-2 py-0.5 text-xs font-medium text-slate-600 ml-auto">
422→                        p.{criterion.page_number}
423→                    </span>
424→                )}
425→            </div>
426→
427→            {/* Body */}
428→            {editMode === 'text' ? (
429→                <div className="space-y-3 mb-3">
430→                    <div>
431→                        <label
432→                            htmlFor={`edit-text-${criterion.id}`}
433→                            className="block text-sm font-medium text-muted-foreground mb-1"
434→                        >
435→                            Text
436→                        </label>
437→                        <textarea
438→                            id={`edit-text-${criterion.id}`}
439→                            value={editText}
440→                            onChange={(e) => setEditText(e.target.value)}
441→                            className="w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
442→                            rows={3}
443→                        />
444→                    </div>
445→                    <div className="grid grid-cols-2 gap-3">
446→                        <div>
447→                            <label
448→                                htmlFor={`edit-type-${criterion.id}`}
449→                                className="block text-sm font-medium text-muted-foreground mb-1"
450→                            >
451→                                Type
452→                            </label>
453→                            <select
454→                                id={`edit-type-${criterion.id}`}
455→                                value={editType}
456→                                onChange={(e) => setEditType(e.target.value)}
457→                                className="w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
458→                            >
459→                                <option value="inclusion">Inclusion</option>
460→                                <option value="exclusion">Exclusion</option>
461→                            </select>
462→                        </div>
463→                        <div>
464→                            <label
465→                                htmlFor={`edit-category-${criterion.id}`}
466→                                className="block text-sm font-medium text-muted-foreground mb-1"
467→                            >
468→                                Category
469→                            </label>
470→                            <input
471→                                id={`edit-category-${criterion.id}`}
472→                                type="text"
473→                                value={editCategory}
474→                                onChange={(e) => setEditCategory(e.target.value)}
475→                                className="w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
476→                            />
477→                        </div>
478→                    </div>
479→                    <div>
480→                        <label
481→                            htmlFor={`edit-rationale-${criterion.id}`}
482→                            className="block text-sm font-medium text-muted-foreground mb-1"
483→                        >
484→                            Rationale <span className="text-xs text-gray-400">(optional)</span>
485→                        </label>
486→                        <textarea
487→                            id={`edit-rationale-${criterion.id}`}
488→                            value={rationale}
489→                            onChange={(e) => setRationale(e.target.value)}
490→                            placeholder="Why are you making this change?"
491→                            className="w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
492→                            rows={2}
493→                        />
494→                    </div>
495→                    <div className="flex items-center gap-2">
496→                        <Button size="sm" onClick={handleModifySave} disabled={isSubmitting}>
497→                            {isSubmitting ? (
498→                                <Loader2 className="h-4 w-4 animate-spin mr-1" />
499→                            ) : null}
500→                            Save
501→                        </Button>
502→                        <Button
503→                            size="sm"
504→                            variant="outline"
505→                            onClick={handleModifyCancel}
506→                            disabled={isSubmitting}
507→                        >
508→                            Cancel
509→                        </Button>
510→                    </div>
511→                </div>
512→            ) : editMode === 'structured' ? (
513→                <div className="mb-3">
514→                    <StructuredFieldEditor
515→                        criterionId={criterion.id}
516→                        initialValues={buildInitialValues(criterion)}
517→                        onSave={handleStructuredSave}
518→                        onCancel={() => setEditMode('none')}
519→                        isSubmitting={isSubmitting}
520→                    />
521→                </div>
522→            ) : (
523→                <p
524→                    className={cn(
525→                        'text-sm text-foreground mb-3',
526→                        criterion.page_number != null &&
527→                            'cursor-pointer hover:bg-accent/50 rounded px-1 -mx-1 transition-colors',
528→                        isActive && 'bg-accent/30 rounded px-1 -mx-1'
529→                    )}
530→                    onClick={() => {
531→                        if (criterion.page_number != null && onCriterionClick) {
532→                            onCriterionClick(criterion);
533→                        }
534→                    }}
535→                    onKeyDown={(e) => {
536→                        if (
537→                            (e.key === 'Enter' || e.key === ' ') &&
538→                            criterion.page_number != null &&
539→                            onCriterionClick
540→                        ) {
541→                            onCriterionClick(criterion);
542→                        }
543→                    }}
544→                    role={criterion.page_number != null ? 'button' : undefined}
545→                    tabIndex={criterion.page_number != null ? 0 : undefined}
546→                    title={
547→                        criterion.page_number != null
548→                            ? `Click to view source (page ${criterion.page_number})`
549→                            : undefined
550→                    }
551→                >
552→                    {criterion.text}
553→                </p>
