 8→function formatMappingValue(value: Record<string, unknown>): string {
 9→    if (typeof value !== 'object' || value === null) return '';
10→    if (value.type === 'range')
11→        return `${value.min}–${value.max}${value.unit ? ` ${value.unit}` : ''}`;
12→    if (value.type === 'temporal') return `${value.duration} ${value.unit}`;
13→    if (value.type === 'standard') return `${value.value}${value.unit ? ` ${value.unit}` : ''}`;
14→    return '';
15→}
