  1→import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
  2→
  3→import { useAuthStore } from '../stores/authStore';
  4→
  5→const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:8000';
  6→
  7→async function fetchApi<T>(endpoint: string, options?: RequestInit): Promise<T> {
  8→    // Get auth token from store
  9→    const token = useAuthStore.getState().token;
 10→    const headers: HeadersInit = {
 11→        'Content-Type': 'application/json',
 12→        ...(token ? { Authorization: `Bearer ${token}` } : {}),
 13→        ...(options?.headers || {}),
 14→    };
 15→
 16→    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
 17→        ...options,
 18→        headers,
 19→    });
 20→
 21→    if (response.status === 401) {
 22→        useAuthStore.getState().logout();
 23→        throw new Error('Session expired');
 24→    }
 25→
 26→    if (!response.ok) {
 27→        throw new Error(`API Error: ${response.status} ${response.statusText}`);
 28→    }
 29→
 30→    return response.json();
 31→}
