141→
142→export function useBatchList(page: number, pageSize: number, status?: string) {
143→    const params = new URLSearchParams({
144→        page: String(page),
145→        page_size: String(pageSize),
146→    });
147→    if (status) {
148→        params.set('status', status);
149→    }
150→
151→    return useQuery({
152→        queryKey: ['review-batches', page, pageSize, status],
153→        queryFn: () => fetchApi<BatchListResponse>(`/reviews/batches?${params.toString()}`),
154→    });
155→}
156→
157→export function useBatchesByProtocol(protocolId: string) {
158→    return useQuery({
159→        queryKey: ['review-batches', 'protocol', protocolId],
160→        queryFn: () =>
161→            fetchApi<BatchListResponse>(
162→                `/reviews/batches?protocol_id=${encodeURIComponent(protocolId)}`
163→            ),
164→        enabled: !!protocolId,
165→    });
166→}
167→
168→export function useBatchCriteria(batchId: string, sortBy?: string, sortOrder?: string) {
169→    const params = new URLSearchParams();
170→    if (sortBy) {
171→        params.set('sort_by', sortBy);
172→    }
173→    if (sortOrder) {
174→        params.set('sort_order', sortOrder);
175→    }
176→    const queryString = params.toString();
177→    const url = queryString
178→        ? `/reviews/batches/${batchId}/criteria?${queryString}`
179→        : `/reviews/batches/${batchId}/criteria`;
180→
181→    return useQuery({
182→        queryKey: ['batch-criteria', batchId, sortBy, sortOrder],
183→        queryFn: () => fetchApi<Criterion[]>(url),
184→        enabled: Boolean(batchId),
185→    });
186→}
187→
188→export function useReviewAction() {
189→    const queryClient = useQueryClient();
190→
191→    return useMutation({
192→        mutationFn: ({ criteriaId, ...body }: ReviewActionRequest & { criteriaId: string }) =>
193→            fetchApi<Criterion>(`/reviews/criteria/${criteriaId}/action`, {
194→                method: 'POST',
195→                body: JSON.stringify(body),
196→            }),
197→        onSuccess: () => {
198→            queryClient.invalidateQueries({ queryKey: ['batch-criteria'] });
199→            queryClient.invalidateQueries({ queryKey: ['review-batches'] });
200→        },
201→    });
202→}
203→
204→export function usePdfUrl(protocolId: string) {
205→    return useQuery({
206→        queryKey: ['pdf-url', protocolId],
207→        queryFn: () => fetchApi<PdfUrlResponse>(`/reviews/protocols/${protocolId}/pdf-url`),
208→        enabled: Boolean(protocolId),
209→        staleTime: 50 * 60 * 1000,
210→    });
211→}
212→
213→export function useAuditLog(
214→    page: number,
215→    pageSize: number,
216→    targetType?: string,
217→    targetId?: string,
218→    batchId?: string
219→) {
220→    const params = new URLSearchParams({
221→        page: String(page),
222→        page_size: String(pageSize),
223→    });
224→    if (targetType) {
225→        params.set('target_type', targetType);
226→    }
227→    if (targetId) {
228→        params.set('target_id', targetId);
229→    }
230→    if (batchId) {
231→        params.set('batch_id', batchId);
232→    }
233→
234→    return useQuery({
235→        queryKey: ['audit-log', page, pageSize, targetType, targetId, batchId],
236→        queryFn: () => fetchApi<AuditLogListResponse>(`/reviews/audit-log?${params.toString()}`),
237→        staleTime: 30_000,
238→    });
