 17→import TextBoundaryAdjuster from './TextBoundaryAdjuster';
 18→import { StructuredFieldEditor } from './structured-editor/StructuredFieldEditor';
 19→import type { StructuredFieldFormValues } from './structured-editor/types';
 20→import { Button } from './ui/Button';
 21→
 22→interface CriterionModifyDialogProps {
 23→    open: boolean;
 24→    onOpenChange: (open: boolean) => void;
 25→    criterion: Criterion;
 26→    onAction: (action: ReviewActionRequest) => void;
 27→    isSubmitting: boolean;
 28→    pdfUrl?: string;
 29→}
 30→
 31→export default function CriterionModifyDialog({
 32→    open,
 33→    onOpenChange,
 34→    criterion,
 35→    onAction,
 36→    isSubmitting,
 37→    pdfUrl,
 38→}: CriterionModifyDialogProps) {
 39→    const [modifiedText, setModifiedText] = useState(criterion.text);
 40→    const [structuredValues, setStructuredValues] = useState<StructuredFieldFormValues | null>(
 41→        null
 42→    );
 43→
 44→    // Reset state when dialog closes
 45→    useEffect(() => {
 46→        if (!open) {
 47→            setModifiedText(criterion.text);
 48→            setStructuredValues(null);
 49→        }
 50→    }, [open, criterion.text]);
 51→
 52→    const handleTextBoundaryChange = useCallback((newText: string) => {
 53→        setModifiedText(newText);
 54→    }, []);
 55→
 56→    function handleStructuredSave(values: StructuredFieldFormValues) {
 57→        setStructuredValues(values);
 58→    }
 59→
 60→    function handleSave() {
 61→        const action: ReviewActionRequest = {
 62→            action: 'modify',
 63→            reviewer_id: 'current-user',
 64→        };
 65→
 66→        // Include modified text if changed
 67→        if (modifiedText !== criterion.text) {
 68→            action.modified_text = modifiedText;
 69→        }
 70→
 71→        // Include structured field changes if any
 72→        if (structuredValues) {
 73→            action.modified_structured_fields = {
 74→                field_mappings: structuredValues.mappings as Array<{
 75→                    entity: string;
 76→                    relation: string;
 77→                    value: unknown;
 78→                }>,
 79→            };
 80→        }
 81→
 82→        onAction(action);
 83→        onOpenChange(false);
 84→    }
 85→
 86→    return (
 87→        <Dialog.Root open={open} onOpenChange={onOpenChange}>
 88→            <Dialog.Portal>
 89→                <Dialog.Overlay className="fixed inset-0 bg-black/50 z-50" />
 90→                <Dialog.Content className="fixed top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 bg-white dark:bg-card rounded-lg p-6 max-w-2xl w-full z-50 shadow-lg max-h-[85vh] overflow-y-auto">
 91→                    {/* Title bar */}
 92→                    <Dialog.Title className="text-base font-semibold mb-1">
 93→                        Modify Criterion
 94→                    </Dialog.Title>
 95→                    <div className="flex flex-wrap items-center gap-2 mb-4">
 96→                        <CriteriaTypeBadge type={criterion.criteria_type} />
 97→                        {criterion.category && (
 98→                            <span className="inline-flex items-center rounded-full bg-gray-100 px-2.5 py-0.5 text-xs font-medium text-gray-700">
 99→                                {criterion.category}
100→                            </span>
101→                        )}
102→                        <ConfidenceBadge confidence={criterion.confidence} />
103→                        <CriterionReviewStatusBadge status={criterion.review_status} />
104→                    </div>
105→
106→                    {/* System Selected panel */}
107→                    <div className="bg-muted/30 border rounded-lg p-4 mb-4">
108→                        <h3 className="text-xs font-semibold text-muted-foreground uppercase tracking-wide mb-3">
109→                            System Selected
110→                        </h3>
111→
112→                        {/* Field mappings (read-only) */}
113→                        <FieldMappingBadges criterion={criterion} />
114→
115→                        {/* Temporal constraint */}
116→                        {criterion.temporal_constraint &&
117→                            formatTemporalConstraint(criterion.temporal_constraint) && (
118→                                <div className="mb-2 flex items-center gap-1.5">
119→                                    <Clock className="h-3.5 w-3.5 text-indigo-600" />
120→                                    <span className="inline-flex items-center rounded-full bg-indigo-100 px-2.5 py-0.5 text-xs font-medium text-indigo-800">
121→                                        {formatTemporalConstraint(criterion.temporal_constraint)}
122→                                    </span>
123→                                </div>
124→                            )}
125→
126→                        {/* Numeric thresholds */}
127→                        {extractThresholdsList(criterion.numeric_thresholds).length > 0 && (
128→                            <div className="mb-2 flex flex-wrap items-center gap-1.5">
129→                                <Hash className="h-3.5 w-3.5 text-teal-600" />
130→                                {extractThresholdsList(criterion.numeric_thresholds).map(
131→                                    (threshold) => {
132→                                        const text = formatNumericThreshold(threshold);
133→                                        return text ? (
134→                                            <span
135→                                                key={text}
136→                                                className="inline-flex items-center rounded-full bg-teal-100 px-2.5 py-0.5 text-xs font-medium text-teal-800"
137→                                            >
138→                                                {text}
139→                                            </span>
140→                                        ) : null;
141→                                    }
142→                                )}
143→                            </div>
144→                        )}
145→
146→                        {/* Entities with terminology badges */}
147→                        {criterion.entities && criterion.entities.length > 0 && (
148→                            <div className="mt-2">
149→                                <p className="text-xs text-muted-foreground mb-1">Entities:</p>
150→                                <div className="flex flex-wrap gap-1.5">
151→                                    {criterion.entities.map((entity) => (
152→                                        <span
153→                                            key={entity.id}
154→                                            className="inline-flex items-center gap-1"
155→                                        >
156→                                            <span className="text-xs font-medium">
157→                                                {entity.text}
158→                                            </span>
159→                                            {entity.snomed_code && (
160→                                                <TerminologyBadge
161→                                                    system="snomed"
162→                                                    code={entity.snomed_code}
163→                                                />
164→                                            )}
165→                                            {entity.umls_cui && (
166→                                                <TerminologyBadge
167→                                                    system="umls"
168→                                                    code={entity.umls_cui}
169→                                                />
170→                                            )}
171→                                        </span>
172→                                    ))}
173→                                </div>
174→                            </div>
175→                        )}
176→                    </div>
177→
178→                    {/* Text Boundary Adjuster */}
179→                    {pdfUrl && criterion.page_number != null && (
180→                        <div className="mb-4">
181→                            <h3 className="text-xs font-semibold text-muted-foreground uppercase tracking-wide mb-2">
182→                                Adjust Text Boundary
183→                            </h3>
184→                            <TextBoundaryAdjuster
185→                                criterionText={criterion.text}
186→                                pageNumber={criterion.page_number}
187→                                pdfUrl={pdfUrl}
188→                                onChange={handleTextBoundaryChange}
189→                            />
190→                        </div>
191→                    )}
192→
193→                    {/* Structured Fields section */}
194→                    <div className="mb-4">
195→                        <h3 className="text-xs font-semibold text-muted-foreground uppercase tracking-wide mb-2">
196→                            Structured Fields
197→                        </h3>
198→                        <StructuredFieldEditor
199→                            criterionId={criterion.id}
200→                            initialValues={buildInitialValues(criterion)}
201→                            onSave={handleStructuredSave}
202→                            onCancel={() => {
203→                                /* no-op in dialog context, cancel handled by dialog */
204→                            }}
205→                            isSubmitting={isSubmitting}
206→                        />
207→                    </div>
208→
209→                    {/* Footer */}
210→                    <div className="flex items-center justify-end gap-2 pt-3 border-t">
211→                        <Button
212→                            type="button"
213→                            size="sm"
214→                            variant="outline"
215→                            onClick={() => onOpenChange(false)}
216→                            disabled={isSubmitting}
217→                        >
218→                            Cancel
219→                        </Button>
220→                        <Button size="sm" onClick={handleSave} disabled={isSubmitting}>
221→                            {isSubmitting ? (
222→                                <Loader2 className="h-4 w-4 animate-spin mr-1" />
223→                            ) : null}
224→                            Save
225→                        </Button>
226→                    </div>
227→                </Dialog.Content>
228→            </Dialog.Portal>
229→        </Dialog.Root>
230→    );
231→}
