 2→
 3→export type RelationOperator =
 4→    | '='
 5→    | '!='
 6→    | '>'
 7→    | '>='
 8→    | '<'
 9→    | '<='
10→    | 'within'
11→    | 'not_in_last'
12→    | 'contains'
13→    | 'not_contains';
14→
15→export type RelationCategory = 'standard' | 'range' | 'temporal';
16→
17→export type TemporalUnit = 'days' | 'weeks' | 'months' | 'years';
18→
19→export interface StandardValue {
20→    type: 'standard';
21→    value: string;
22→    unit: string;
23→}
24→
25→export interface RangeValue {
26→    type: 'range';
27→    min: string;
28→    max: string;
29→    unit: string;
30→}
31→
32→export interface TemporalValue {
33→    type: 'temporal';
34→    duration: string;
35→    unit: TemporalUnit;
36→}
37→
38→export type FieldValue = StandardValue | RangeValue | TemporalValue;
39→
40→// Single field mapping with all possible fields
41→export interface FieldMapping {
42→    entity: string;
43→    relation: RelationOperator | '';
44→    value: FieldValue;
45→}
46→
47→// Form values now contain an array of mappings
48→export interface StructuredFieldFormValues {
49→    mappings: FieldMapping[];
50→}
51→
52→export interface StructuredFieldEditorProps {
53→    criterionId: string;
54→    initialValues?: Partial<StructuredFieldFormValues>;
55→    onSave: (values: StructuredFieldFormValues) => void;
56→    onCancel: () => void;
57→    isSubmitting?: boolean;
58→}
59→

  1→// Main structured field editor component with multiple entity/relation/value mappings
  2→
  3→import { Loader2, Plus, Trash2 } from 'lucide-react';
  4→import { useEffect, useRef } from 'react';
  5→import { Controller, useFieldArray, useForm } from 'react-hook-form';
  6→
  7→import { Button } from '../ui/Button';
  8→import { RelationSelect } from './RelationSelect';
  9→import { ValueInput } from './ValueInput';
 10→import {
 11→    DEFAULT_FIELD_VALUES,
 12→    DEFAULT_MAPPING,
 13→    RELATION_CATEGORY_MAP,
 14→    getDefaultValueForCategory,
 15→} from './constants';
 16→import type {
 17→    RelationOperator,
 18→    StructuredFieldEditorProps,
 19→    StructuredFieldFormValues,
 20→} from './types';
 21→
 22→export function StructuredFieldEditor({
 23→    criterionId,
 24→    initialValues,
 25→    onSave,
 26→    onCancel,
 27→    isSubmitting = false,
 28→}: StructuredFieldEditorProps) {
 29→    const { control, handleSubmit, watch, setValue } = useForm<StructuredFieldFormValues>({
 30→        defaultValues: initialValues ?? DEFAULT_FIELD_VALUES,
 31→    });
 32→
 33→    const { fields, append, remove } = useFieldArray({
 34→        control,
 35→        name: 'mappings',
 36→    });
 37→
 38→    // Watch all mappings for validation
 39→    const allMappings = watch('mappings');
 40→
 41→    // Track previous relations for each mapping to detect changes
 42→    const previousRelationsRef = useRef<Array<RelationOperator | ''>>([]);
 43→
 44→    // State cleanup on relation change - prevent state leak between categories
 45→    useEffect(() => {
 46→        allMappings.forEach((mapping, index) => {
 47→            const currentRelation = mapping.relation;
 48→            const previousRelation = previousRelationsRef.current[index] ?? '';
 49→
 50→            // Skip on initial mount (previousRelation is empty string initially)
 51→            if (previousRelation === '' && currentRelation === '') {
 52→                return;
 53→            }
 54→
 55→            // If relation actually changed
 56→            if (previousRelation !== currentRelation && currentRelation !== '') {
 57→                const newCategory = RELATION_CATEGORY_MAP[currentRelation as RelationOperator];
 58→                const currentCategory = mapping.value.type;
 59→
 60→                // If category changed, reset value to default for new category
 61→                if (newCategory !== currentCategory) {
 62→                    setValue(`mappings.${index}.value`, getDefaultValueForCategory(newCategory));
 63→                }
 64→            }
 65→
 66→            // Update ref for next change detection
 67→            previousRelationsRef.current[index] = currentRelation;
 68→        });
 69→    }, [allMappings, setValue]);
 70→
 71→    // Check if we can add a new mapping (last mapping must have entity filled)
 72→    const canAddMapping = () => {
 73→        if (fields.length === 0) return true;
 74→        const lastMapping = allMappings[allMappings.length - 1];
 75→        return lastMapping && lastMapping.entity.trim() !== '';
 76→    };
 77→
 78→    const handleAddMapping = () => {
 79→        if (canAddMapping()) {
 80→            append(DEFAULT_MAPPING);
 81→        }
 82→    };
 83→
 84→    return (
 85→        <div className="space-y-4">
 86→            {/* Mapping cards */}
 87→            {fields.map((field, index) => {
 88→                const mapping = allMappings[index];
 89→                const relationCategory =
 90→                    mapping?.relation !== ''
 91→                        ? RELATION_CATEGORY_MAP[mapping.relation as RelationOperator]
 92→                        : null;
 93→
 94→                return (
 95→                    <div key={field.id} className="border rounded-lg p-3 mb-3 relative">
 96→                        {/* Mapping label and remove button */}
 97→                        <div className="flex items-center justify-between mb-3">
 98→                            <span className="text-sm font-medium text-muted-foreground">
 99→                                Mapping {index + 1}
100→                            </span>
101→                            <Button
102→                                type="button"
103→                                size="sm"
104→                                variant="ghost"
105→                                onClick={() => remove(index)}
106→                                disabled={fields.length === 1 || isSubmitting}
107→                                className="h-6 w-6 p-0"
108→                            >
109→                                <Trash2 className="h-4 w-4 text-red-600" />
110→                            </Button>
111→                        </div>
112→
113→                        {/* Entity field */}
114→                        <div className="mb-3">
115→                            <label
116→                                htmlFor={`entity-${criterionId}-${index}`}
117→                                className="block text-sm font-medium text-muted-foreground mb-1"
118→                            >
119→                                Entity
120→                            </label>
121→                            <Controller
122→                                name={`mappings.${index}.entity`}
123→                                control={control}
124→                                render={({ field }) => (
125→                                    <input
126→                                        id={`entity-${criterionId}-${index}`}
127→                                        type="text"
128→                                        placeholder="e.g., Age, HbA1c, Acetaminophen"
129→                                        {...field}
130→                                        disabled={isSubmitting}
131→                                        className="w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring disabled:opacity-50 disabled:cursor-not-allowed"
132→                                    />
133→                                )}
134→                            />
135→                        </div>
136→
137→                        {/* Relation field */}
138→                        <div className="mb-3">
139→                            <label
140→                                htmlFor={`relation-${criterionId}-${index}`}
141→                                className="block text-sm font-medium text-muted-foreground mb-1"
142→                            >
143→                                Relation
144→                            </label>
145→                            <Controller
146→                                name={`mappings.${index}.relation`}
147→                                control={control}
148→                                render={({ field }) => (
149→                                    <RelationSelect
150→                                        value={field.value}
151→                                        onChange={field.onChange}
152→                                        disabled={isSubmitting}
153→                                    />
154→                                )}
155→                            />
156→                        </div>
157→
158→                        {/* Value field - conditionally rendered based on relation category */}
159→                        {relationCategory && (
160→                            <div>
161→                                <Controller
162→                                    name={`mappings.${index}.value`}
163→                                    control={control}
164→                                    render={({ field }) => (
165→                                        <ValueInput
166→                                            category={relationCategory}
167→                                            value={field.value}
168→                                            onChange={field.onChange}
169→                                            disabled={isSubmitting}
170→                                        />
171→                                    )}
172→                                />
173→                            </div>
174→                        )}
175→                    </div>
176→                );
177→            })}
178→
179→            {/* Add Mapping button */}
180→            <div className="mb-3">
181→                <Button
182→                    type="button"
183→                    size="sm"
184→                    variant="outline"
185→                    onClick={handleAddMapping}
186→                    disabled={!canAddMapping() || isSubmitting}
187→                    className="w-full"
188→                >
189→                    <Plus className="h-4 w-4 mr-1" />
190→                    Add Mapping
191→                </Button>
192→                {!canAddMapping() && fields.length > 0 && (
193→                    <p className="text-xs text-muted-foreground mt-1">
194→                        Complete the current mapping before adding another
195→                    </p>
196→                )}
197→            </div>
198→
199→            {/* Action buttons */}
200→            <div className="flex items-center gap-2 pt-2">
201→                <Button size="sm" onClick={handleSubmit(onSave)} disabled={isSubmitting}>
202→                    {isSubmitting ? <Loader2 className="h-4 w-4 animate-spin mr-1" /> : null}
203→                    Save
204→                </Button>
205→                <Button size="sm" variant="outline" onClick={onCancel} disabled={isSubmitting}>
206→                    Cancel
207→                </Button>
208→            </div>
209→        </div>
210→    );
211→}
212→
213→// Export as both default and named export for flexible importing
214→export default StructuredFieldEditor;
