 1→// Relation configuration and constants
 2→
 3→import type {
 4→    FieldMapping,
 5→    FieldValue,
 6→    RelationCategory,
 7→    RelationOperator,
 8→    StructuredFieldFormValues,
 9→    TemporalUnit,
10→} from './types';
11→
12→export const RELATIONS: Array<{
13→    operator: RelationOperator;
14→    label: string;
15→    category: RelationCategory;
16→}> = [
17→    { operator: '=', label: 'equals (=)', category: 'standard' },
18→    { operator: '!=', label: 'not equals (!=)', category: 'standard' },
19→    { operator: '>', label: 'greater than (>)', category: 'standard' },
20→    { operator: '>=', label: 'greater or equal (>=)', category: 'standard' },
21→    { operator: '<', label: 'less than (<)', category: 'standard' },
22→    { operator: '<=', label: 'less or equal (<=)', category: 'standard' },
23→    { operator: 'within', label: 'within (range)', category: 'range' },
24→    { operator: 'not_in_last', label: 'not in last (temporal)', category: 'temporal' },
25→    { operator: 'contains', label: 'contains', category: 'standard' },
26→    { operator: 'not_contains', label: 'does not contain', category: 'standard' },
27→];
28→
29→export const RELATION_CATEGORY_MAP: Record<RelationOperator, RelationCategory> = RELATIONS.reduce(
30→    (acc, rel) => {
31→        acc[rel.operator] = rel.category;
32→        return acc;
33→    },
34→    {} as Record<RelationOperator, RelationCategory>
35→);
36→
37→export const TEMPORAL_UNITS: Array<{ value: TemporalUnit; label: string }> = [
38→    { value: 'days', label: 'Days' },
39→    { value: 'weeks', label: 'Weeks' },
40→    { value: 'months', label: 'Months' },
41→    { value: 'years', label: 'Years' },
42→];
43→
44→export const DEFAULT_MAPPING: FieldMapping = {
45→    entity: '',
46→    relation: '' as const,
47→    value: { type: 'standard', value: '', unit: '' },
48→};
49→
50→export const DEFAULT_FIELD_VALUES: StructuredFieldFormValues = {
51→    mappings: [DEFAULT_MAPPING],
52→};
53→
54→export function getDefaultValueForCategory(category: RelationCategory): FieldValue {
55→    switch (category) {
56→        case 'standard':
57→            return { type: 'standard', value: '', unit: '' };
58→        case 'range':
59→            return { type: 'range', min: '', max: '', unit: '' };
60→        case 'temporal':
61→            return { type: 'temporal', duration: '', unit: 'days' };
62→    }
63→}
