  1→// Radix UI Select wrapper for relation/operator dropdown
  2→
  3→import * as Select from '@radix-ui/react-select';
  4→import { ChevronDown } from 'lucide-react';
  5→
  6→import { cn } from '../../lib/utils';
  7→import { RELATIONS } from './constants';
  8→import type { RelationOperator } from './types';
  9→
 10→interface RelationSelectProps {
 11→    value: RelationOperator | '';
 12→    onChange: (value: RelationOperator) => void;
 13→    disabled?: boolean;
 14→}
 15→
 16→export function RelationSelect({ value, onChange, disabled }: RelationSelectProps) {
 17→    const selectedRelation = RELATIONS.find((r) => r.operator === value);
 18→    const displayLabel = selectedRelation?.label ?? 'Select relation...';
 19→
 20→    // Group relations by display category
 21→    const comparisonRelations = RELATIONS.filter((r) =>
 22→        ['=', '!=', '>', '>=', '<', '<='].includes(r.operator)
 23→    );
 24→    const rangeRelations = RELATIONS.filter((r) => r.operator === 'within');
 25→    const temporalRelations = RELATIONS.filter((r) => r.operator === 'not_in_last');
 26→    const textMatchRelations = RELATIONS.filter((r) =>
 27→        ['contains', 'not_contains'].includes(r.operator)
 28→    );
 29→
 30→    return (
 31→        <Select.Root
 32→            value={value}
 33→            onValueChange={(newValue) => onChange(newValue as RelationOperator)}
 34→            disabled={disabled}
 35→        >
 36→            <Select.Trigger
 37→                className={cn(
 38→                    'w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background',
 39→                    'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring',
 40→                    'flex items-center justify-between',
 41→                    'disabled:opacity-50 disabled:cursor-not-allowed'
 42→                )}
 43→            >
 44→                <Select.Value>{displayLabel}</Select.Value>
 45→                <Select.Icon>
 46→                    <ChevronDown className="h-4 w-4 opacity-50" />
 47→                </Select.Icon>
 48→            </Select.Trigger>
 49→
 50→            <Select.Portal>
 51→                <Select.Content
 52→                    className="overflow-hidden bg-popover rounded-md border shadow-md z-50"
 53→                    position="popper"
 54→                    sideOffset={4}
 55→                >
 56→                    <Select.Viewport className="p-1">
 57→                        {/* Comparison group */}
 58→                        <Select.Group>
 59→                            <Select.Label className="px-2 py-1.5 text-xs font-semibold text-muted-foreground">
 60→                                Comparison
 61→                            </Select.Label>
 62→                            {comparisonRelations.map((relation) => (
 63→                                <Select.Item
 64→                                    key={relation.operator}
 65→                                    value={relation.operator}
 66→                                    className={cn(
 67→                                        'relative flex cursor-pointer select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none',
 68→                                        'focus:bg-accent focus:text-accent-foreground',
 69→                                        'data-[disabled]:pointer-events-none data-[disabled]:opacity-50'
 70→                                    )}
 71→                                >
 72→                                    <Select.ItemText>{relation.label}</Select.ItemText>
 73→                                </Select.Item>
 74→                            ))}
 75→                        </Select.Group>
 76→
 77→                        {/* Range group */}
 78→                        <Select.Group>
 79→                            <Select.Label className="px-2 py-1.5 text-xs font-semibold text-muted-foreground">
 80→                                Range
 81→                            </Select.Label>
 82→                            {rangeRelations.map((relation) => (
 83→                                <Select.Item
 84→                                    key={relation.operator}
 85→                                    value={relation.operator}
 86→                                    className={cn(
 87→                                        'relative flex cursor-pointer select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none',
 88→                                        'focus:bg-accent focus:text-accent-foreground',
 89→                                        'data-[disabled]:pointer-events-none data-[disabled]:opacity-50'
 90→                                    )}
 91→                                >
 92→                                    <Select.ItemText>{relation.label}</Select.ItemText>
 93→                                </Select.Item>
 94→                            ))}
 95→                        </Select.Group>
 96→
 97→                        {/* Temporal group */}
 98→                        <Select.Group>
 99→                            <Select.Label className="px-2 py-1.5 text-xs font-semibold text-muted-foreground">
100→                                Temporal
101→                            </Select.Label>
102→                            {temporalRelations.map((relation) => (
103→                                <Select.Item
104→                                    key={relation.operator}
105→                                    value={relation.operator}
106→                                    className={cn(
107→                                        'relative flex cursor-pointer select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none',
108→                                        'focus:bg-accent focus:text-accent-foreground',
109→                                        'data-[disabled]:pointer-events-none data-[disabled]:opacity-50'
110→                                    )}
111→                                >
112→                                    <Select.ItemText>{relation.label}</Select.ItemText>
113→                                </Select.Item>
114→                            ))}
115→                        </Select.Group>
116→
117→                        {/* Text Match group */}
118→                        <Select.Group>
119→                            <Select.Label className="px-2 py-1.5 text-xs font-semibold text-muted-foreground">
120→                                Text Match
121→                            </Select.Label>
122→                            {textMatchRelations.map((relation) => (
123→                                <Select.Item
124→                                    key={relation.operator}
125→                                    value={relation.operator}
126→                                    className={cn(
127→                                        'relative flex cursor-pointer select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none',
128→                                        'focus:bg-accent focus:text-accent-foreground',
129→                                        'data-[disabled]:pointer-events-none data-[disabled]:opacity-50'
130→                                    )}
131→                                >
132→                                    <Select.ItemText>{relation.label}</Select.ItemText>
133→                                </Select.Item>
134→                            ))}
135→                        </Select.Group>
136→                    </Select.Viewport>
137→                </Select.Content>
138→            </Select.Portal>
139→        </Select.Root>
140→    );
141→}
