 93→function formatTemporalConstraint(tc: Record<string, unknown>): string {
 94→    const duration = 'duration' in tc ? (tc.duration as string) : null;
 95→    const relation = 'relation' in tc ? (tc.relation as string) : null;
 96→    const referencePoint = 'reference_point' in tc ? (tc.reference_point as string) : null;
 97→
 98→    if (!duration) return '';
 99→
100→    const relationMap: Record<string, string> = {
101→        within: 'Within',
102→        before: 'Before',
103→        after: 'After',
104→        at_least: 'At least',
105→    };
106→
107→    const relationText = relation ? (relationMap[relation] || relation) : '';
108→    const parts = [relationText, duration];
109→    if (referencePoint) parts.push('of', referencePoint);
110→
111→    return parts.filter(Boolean).join(' ');
112→}
113→
114→function formatNumericThreshold(threshold: Record<string, unknown>): string {
115→    const value = 'value' in threshold ? threshold.value : null;
116→    const unit = 'unit' in threshold ? (threshold.unit as string) : '';
117→    const comparator = 'comparator' in threshold ? (threshold.comparator as string) : '';
118→    const upperValue = 'upper_value' in threshold ? threshold.upper_value : null;
119→
120→    if (value === null) return '';
121→
122→    if (comparator === 'range' && upperValue !== null) {
123→        return `${value}-${upperValue} ${unit}`.trim();
124→    }
125→
126→    return `${comparator}${value} ${unit}`.trim();
127→}
128→
129→function extractThresholdsList(
130→    nt: Record<string, unknown> | null
131→): Array<Record<string, unknown>> {
132→    if (!nt) return [];
133→
134→    // Shape 1: {"thresholds": [...]} wrapper object
135→    if ('thresholds' in nt && Array.isArray(nt.thresholds)) {
136→        return nt.thresholds as Array<Record<string, unknown>>;
137→    }
138→
139→    // Shape 2: raw array stored directly
140→    if (Array.isArray(nt)) {
141→        return nt as Array<Record<string, unknown>>;
142→    }
143→
144→    // Shape 3: single threshold object without wrapper
145→    if ('value' in nt && 'comparator' in nt) {
146→        return [nt];
147→    }
148→
149→    return [];
150→}
151→
152→/** Map extraction comparator strings to RelationOperator. */
153→function mapComparator(comparator: string): RelationOperator | '' {
154→    const map: Record<string, RelationOperator> = {
155→        '>=': '>=',
156→        '<=': '<=',
157→        '>': '>',
158→        '<': '<',
159→        '==': '=',
160→        '=': '=',
161→        'range': 'within',
162→    };
163→    return map[comparator] ?? '';
164→}
165→
166→/** Parse a duration string like "6 months" into {value, unit}. */
167→function parseDuration(duration: string): { value: string; unit: TemporalUnit } {
168→    const match = duration.match(/^(\d+)\s*(days?|weeks?|months?|years?)$/i);
169→    if (match) {
170→        const raw = match[2].toLowerCase().replace(/s$/, '');
171→        const unitMap: Record<string, TemporalUnit> = {
172→            day: 'days',
173→            week: 'weeks',
174→            month: 'months',
175→            year: 'years',
176→        };
177→        return { value: match[1], unit: unitMap[raw] ?? 'days' };
178→    }
179→    return { value: duration, unit: 'days' };
180→}
181→
182→/**
183→ * Build initial form values for the structured editor from a criterion's
184→ * AI-extracted data (numeric_thresholds, temporal_constraint, conditions).
185→ */
186→function buildInitialValues(criterion: Criterion): StructuredFieldFormValues {
187→    // Priority 1: existing field_mappings from a previous structured edit
188→    const cond = criterion.conditions as Record<string, unknown> | null;
189→    if (cond && 'field_mappings' in cond && Array.isArray(cond.field_mappings)) {
190→        const fms = cond.field_mappings as Array<Record<string, unknown>>;
191→        const mappings: FieldMapping[] = fms.map((fm) => {
192→            const rel = (fm.relation as string) ?? '';
193→            const rawVal = fm.value as Record<string, unknown> | undefined;
194→            let value: FieldValue = { type: 'standard', value: '', unit: '' };
195→            if (rawVal && rawVal.type === 'range') {
196→                value = {
197→                    type: 'range',
198→                    min: String(rawVal.min ?? ''),
199→                    max: String(rawVal.max ?? ''),
200→                    unit: String(rawVal.unit ?? ''),
201→                };
202→            } else if (rawVal && rawVal.type === 'temporal') {
203→                value = {
204→                    type: 'temporal',
205→                    duration: String(rawVal.duration ?? ''),
206→                    unit: (rawVal.unit as TemporalUnit) ?? 'days',
207→                };
208→            } else if (rawVal && rawVal.type === 'standard') {
209→                value = {
210→                    type: 'standard',
211→                    value: String(rawVal.value ?? ''),
212→                    unit: String(rawVal.unit ?? ''),
213→                };
214→            }
215→            return {
216→                entity: String(fm.entity ?? ''),
217→                relation: (rel as RelationOperator) || '',
218→                value,
219→            };
220→        });
221→        if (mappings.length > 0) return { mappings };
222→    }
223→
224→    // Priority 2: infer from AI-extracted data (entities + thresholds + temporal)
225→    const mappings: FieldMapping[] = [];
226→
227→    // Grounded entities provide the "what" (entity name + UMLS/SNOMED IDs)
228→    // Thresholds provide the "how" (comparator + value + unit)
229→    // Match them: Lab_Value/Biomarker entities → numeric thresholds,
230→    // Demographic entities → age-related thresholds, etc.
231→    const entities = criterion.entities ?? [];
232→    const thresholds = extractThresholdsList(criterion.numeric_thresholds);
233→
234→    // Build entity label: prefer UMLS preferred_term, fallback to extracted text
235→    const entityLabel = (e: { preferred_term: string | null; text: string }) =>
236→        e.preferred_term || e.text;
237→
238→    // Simple matching: pair thresholds with relevant entities by type
239→    const measurableEntities = entities.filter(
240→        (e) => e.entity_type === 'Lab_Value' || e.entity_type === 'Biomarker' || e.entity_type === 'Demographic'
241→    );
242→
243→    for (let i = 0; i < thresholds.length; i++) {
244→        const t = thresholds[i];
245→        const comparator = (t.comparator as string) ?? '';
246→        const val = t.value as number | null;
247→        const unit = (t.unit as string) ?? '';
248→        const upperVal = t.upper_value as number | null;
249→
250→        if (val === null || val === undefined) continue;
251→
252→        // Use matching entity if available (positional pairing)
253→        const matchedEntity = measurableEntities[i];
254→        const entity = matchedEntity ? entityLabel(matchedEntity) : '';
255→
256→        if (comparator === 'range' && upperVal != null) {
257→            mappings.push({
258→                entity,
259→                relation: 'within' as RelationOperator,
260→                value: { type: 'range', min: String(val), max: String(upperVal), unit },
261→            });
262→        } else {
263→            const relation = mapComparator(comparator);
264→            mappings.push({
265→                entity,
266→                relation,
267→                value: { type: 'standard', value: String(val), unit },
268→            });
269→        }
270→    }
271→
272→    // Entities without a matching threshold get their own mapping (entity only)
273→    const unmatchedEntities = entities.filter(
274→        (e) => e.entity_type === 'Condition' || e.entity_type === 'Medication' || e.entity_type === 'Procedure'
275→    );
276→    for (const e of unmatchedEntities) {
277→        mappings.push({
278→            entity: entityLabel(e),
279→            relation: '',
280→            value: { type: 'standard', value: '', unit: '' },
281→        });
282→    }
283→
284→    // From temporal constraint
285→    if (criterion.temporal_constraint) {
286→        const tc = criterion.temporal_constraint;
287→        const duration = tc.duration as string | undefined;
288→        const referencePoint = tc.reference_point as string | undefined;
289→
290→        if (duration) {
291→            const parsed = parseDuration(duration);
292→            mappings.push({
293→                entity: referencePoint ?? '',
294→                relation: 'not_in_last' as RelationOperator,
295→                value: { type: 'temporal', duration: parsed.value, unit: parsed.unit },
296→            });
297→        }
298→    }
299→
300→    if (mappings.length === 0) return DEFAULT_FIELD_VALUES;
301→    return { mappings };
302→}
