304→type EditMode = 'none' | 'text' | 'structured';
305→
306→export default function CriterionCard({ criterion, onAction, isSubmitting, onCriterionClick, isActive }: CriterionCardProps) {
307→    const [editMode, setEditMode] = useState<EditMode>('none');
308→    const [editText, setEditText] = useState(criterion.text);
309→    const [editType, setEditType] = useState(criterion.criteria_type);
310→    const [editCategory, setEditCategory] = useState(criterion.category ?? '');
311→    const [rationale, setRationale] = useState('');
312→
313→    const isLowConfidence = criterion.confidence < 0.7;
314→
315→    // Sync local edit state when criterion prop changes (after mutation response)
316→    useEffect(() => {
317→        setEditText(criterion.text);
318→        setEditType(criterion.criteria_type);
319→        setEditCategory(criterion.category ?? '');
320→    }, [criterion.text, criterion.criteria_type, criterion.category]);
321→
322→    function handleApprove() {
323→        onAction(criterion.id, {
324→            action: 'approve',
325→            reviewer_id: 'current-user',
326→        });
327→    }
328→
329→    function handleReject() {
330→        onAction(criterion.id, {
331→            action: 'reject',
332→            reviewer_id: 'current-user',
333→        });
334→    }
335→
336→    function handleModifySave() {
337→        onAction(criterion.id, {
338→            action: 'modify',
339→            reviewer_id: 'current-user',
340→            modified_text: editText,
341→            modified_type: editType,
342→            modified_category: editCategory || undefined,
343→            comment: rationale || undefined,
344→        });
345→        setRationale('');
346→        setEditMode('none');
347→    }
348→
349→    function handleModifyCancel() {
350→        setEditText(criterion.text);
351→        setEditType(criterion.criteria_type);
352→        setEditCategory(criterion.category ?? '');
353→        setRationale('');
354→        setEditMode('none');
355→    }
356→
357→    function handleStructuredSave(values: { mappings: Array<{ entity: string; relation: string; value: unknown }> }) {
358→        // Convert StructuredFieldFormValues to the modified_structured_fields payload
359→        // Extract the mappings array and send as field_mappings
360→        onAction(criterion.id, {
361→            action: 'modify',
362→            reviewer_id: 'current-user',
363→            modified_structured_fields: {
364→                field_mappings: values.mappings,
365→            },
366→        });
367→        setEditMode('none');
368→    }
369→
370→    return (
371→        <div
372→            className={cn(
373→                'rounded-lg border bg-card p-4 shadow-sm',
374→                isLowConfidence && 'border-l-4 border-l-red-300'
375→            )}
376→        >
377→            {/* Header row */}
378→            <div className="flex flex-wrap items-center gap-2 mb-3">
379→                <CriteriaTypeBadge type={criterion.criteria_type} />
380→                {criterion.category && (
381→                    <span className="inline-flex items-center rounded-full bg-gray-100 px-2.5 py-0.5 text-xs font-medium text-gray-700">
382→                        {criterion.category}
383→                    </span>
384→                )}
385→                <ConfidenceBadge confidence={criterion.confidence} />
386→                <ReviewStatusBadge status={criterion.review_status} />
387→                {criterion.page_number != null && (
388→                    <span className="inline-flex items-center rounded-full bg-slate-100 px-2 py-0.5 text-xs font-medium text-slate-600 ml-auto">
389→                        p.{criterion.page_number}
390→                    </span>
391→                )}
392→            </div>
393→
394→            {/* Body */}
395→            {editMode === 'text' ? (
396→                <div className="space-y-3 mb-3">
397→                    <div>
398→                        <label
399→                            htmlFor={`edit-text-${criterion.id}`}
400→                            className="block text-sm font-medium text-muted-foreground mb-1"
401→                        >
402→                            Text
403→                        </label>
404→                        <textarea
405→                            id={`edit-text-${criterion.id}`}
406→                            value={editText}
407→                            onChange={(e) => setEditText(e.target.value)}
408→                            className="w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
409→                            rows={3}
410→                        />
411→                    </div>
412→                    <div className="grid grid-cols-2 gap-3">
413→                        <div>
414→                            <label
415→                                htmlFor={`edit-type-${criterion.id}`}
416→                                className="block text-sm font-medium text-muted-foreground mb-1"
417→                            >
418→                                Type
419→                            </label>
420→                            <select
421→                                id={`edit-type-${criterion.id}`}
422→                                value={editType}
423→                                onChange={(e) => setEditType(e.target.value)}
424→                                className="w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
425→                            >
426→                                <option value="inclusion">Inclusion</option>
427→                                <option value="exclusion">Exclusion</option>
428→                            </select>
429→                        </div>
430→                        <div>
431→                            <label
432→                                htmlFor={`edit-category-${criterion.id}`}
433→                                className="block text-sm font-medium text-muted-foreground mb-1"
434→                            >
435→                                Category
436→                            </label>
437→                            <input
438→                                id={`edit-category-${criterion.id}`}
439→                                type="text"
440→                                value={editCategory}
441→                                onChange={(e) => setEditCategory(e.target.value)}
442→                                className="w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
443→                            />
444→                        </div>
445→                    </div>
446→                    <div>
447→                        <label
448→                            htmlFor={`edit-rationale-${criterion.id}`}
449→                            className="block text-sm font-medium text-muted-foreground mb-1"
450→                        >
451→                            Rationale <span className="text-xs text-gray-400">(optional)</span>
452→                        </label>
453→                        <textarea
454→                            id={`edit-rationale-${criterion.id}`}
455→                            value={rationale}
456→                            onChange={(e) => setRationale(e.target.value)}
457→                            placeholder="Why are you making this change?"
458→                            className="w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
459→                            rows={2}
460→                        />
461→                    </div>
462→                    <div className="flex items-center gap-2">
463→                        <Button size="sm" onClick={handleModifySave} disabled={isSubmitting}>
464→                            {isSubmitting ? (
465→                                <Loader2 className="h-4 w-4 animate-spin mr-1" />
466→                            ) : null}
467→                            Save
468→                        </Button>
469→                        <Button
470→                            size="sm"
471→                            variant="outline"
472→                            onClick={handleModifyCancel}
473→                            disabled={isSubmitting}
474→                        >
475→                            Cancel
476→                        </Button>
477→                    </div>
478→                </div>
479→            ) : editMode === 'structured' ? (
480→                <div className="mb-3">
481→                    <StructuredFieldEditor
482→                        criterionId={criterion.id}
483→                        initialValues={buildInitialValues(criterion)}
484→                        onSave={handleStructuredSave}
485→                        onCancel={() => setEditMode('none')}
486→                        isSubmitting={isSubmitting}
487→                    />
488→                </div>
489→            ) : (
490→                <p
491→                    className={cn(
492→                        "text-sm text-foreground mb-3",
493→                        criterion.page_number != null && "cursor-pointer hover:bg-accent/50 rounded px-1 -mx-1 transition-colors",
494→                        isActive && "bg-accent/30 rounded px-1 -mx-1"
495→                    )}
496→                    onClick={() => {
497→                        if (criterion.page_number != null && onCriterionClick) {
498→                            onCriterionClick(criterion);
499→                        }
500→                    }}
501→                    title={criterion.page_number != null ? `Click to view source (page ${criterion.page_number})` : undefined}
502→                >
503→                    {criterion.text}
504→                </p>
505→            )}
506→
507→            {/* Assertion status tag */}
508→            {criterion.assertion_status && criterion.assertion_status !== 'PRESENT' && (
509→                <div className="mb-3">
510→                    <span className="inline-flex items-center rounded-full bg-purple-100 px-2.5 py-0.5 text-xs font-medium text-purple-800">
511→                        {criterion.assertion_status}
512→                    </span>
513→                </div>
514→            )}
515→
516→            {/* Temporal constraint */}
517→            {criterion.temporal_constraint && formatTemporalConstraint(criterion.temporal_constraint) && (
518→                <div className="mb-3 flex items-center gap-1.5">
519→                    <Clock className="h-3.5 w-3.5 text-indigo-600" />
520→                    <span className="inline-flex items-center rounded-full bg-indigo-100 px-2.5 py-0.5 text-xs font-medium text-indigo-800">
521→                        {formatTemporalConstraint(criterion.temporal_constraint)}
522→                    </span>
523→                </div>
524→            )}
525→
