 59→function formatRelativeTime(dateStr: string): string {
 60→    const date = new Date(dateStr);
 61→    const now = new Date();
 62→    const diffMs = now.getTime() - date.getTime();
 63→    const diffSec = Math.floor(diffMs / 1000);
 64→    const diffMin = Math.floor(diffSec / 60);
 65→    const diffHr = Math.floor(diffMin / 60);
 66→    const diffDay = Math.floor(diffHr / 24);
 67→
 68→    if (diffSec < 60) return 'just now';
 69→    if (diffMin < 60) return `${diffMin}m ago`;
 70→    if (diffHr < 24) return `${diffHr}h ago`;
 71→    if (diffDay < 30) return `${diffDay}d ago`;
 72→    return date.toLocaleDateString();
 73→}
