 33→// --- TypeScript interfaces ---
 34→
 35→export interface Protocol {
 36→    id: string;
 37→    title: string;
 38→    file_uri: string;
 39→    status:
 40→        | 'uploaded'
 41→        | 'extracting'
 42→        | 'extraction_failed'
 43→        | 'grounding'
 44→        | 'grounding_failed'
 45→        | 'pending_review'
 46→        | 'processing'
 47→        | 'complete'
 48→        | 'dead_letter'
 49→        | 'archived';
 50→    error_reason: string | null;
