101→export function useUploadProtocol() {
102→    const queryClient = useQueryClient();
103→
104→    return useMutation({
105→        mutationFn: async ({ file }: { file: File }) => {
106→            // Step 1: Get signed upload URL from backend
107→            const uploadResp = await fetchApi<UploadResponse>('/protocols/upload', {
108→                method: 'POST',
109→                body: JSON.stringify({
110→                    filename: file.name,
111→                    content_type: file.type,
112→                    file_size_bytes: file.size,
113→                }),
114→            });
115→
116→            // Step 2: Upload file directly to GCS via signed URL
117→            const putResp = await fetch(uploadResp.upload_url, {
118→                method: 'PUT',
119→                headers: { 'Content-Type': 'application/pdf' },
120→                body: file,
121→            });
122→
123→            if (!putResp.ok) {
124→                throw new Error(`Upload failed: ${putResp.status} ${putResp.statusText}`);
125→            }
126→
127→            // Step 3: Read file as base64 for quality scoring, then confirm
128→            const arrayBuffer = await file.arrayBuffer();
129→            const bytes = new Uint8Array(arrayBuffer);
130→            let binary = '';
131→            for (let i = 0; i < bytes.length; i++) {
132→                binary += String.fromCharCode(bytes[i]);
133→            }
134→            const pdfBase64 = btoa(binary);
135→
136→            await fetchApi(`/protocols/${uploadResp.protocol_id}/confirm-upload`, {
137→                method: 'POST',
138→                body: JSON.stringify({ pdf_bytes_base64: pdfBase64 }),
139→            });
140→
141→            return uploadResp;
142→        },
143→        onSuccess: () => {
144→            queryClient.invalidateQueries({ queryKey: ['protocols'] });
145→        },
146→    });
147→}
