 75→export function useProtocolList(page: number, pageSize: number, status?: string, deduplicate?: boolean) {
 76→    const params = new URLSearchParams({
 77→        page: String(page),
 78→        page_size: String(pageSize),
 79→    });
 80→    if (status) {
 81→        params.set('status', status);
 82→    }
 83→    if (deduplicate) {
 84→        params.set('deduplicate', 'true');
 85→    }
 86→
 87→    return useQuery({
 88→        queryKey: ['protocols', page, pageSize, status, deduplicate],
 89→        queryFn: () => fetchApi<ProtocolListResponse>(`/protocols?${params.toString()}`),
 90→    });
