105→export function useUploadProtocol() {
106→    const queryClient = useQueryClient();
107→
108→    return useMutation({
109→        mutationFn: async ({ file }: { file: File }) => {
110→            // Step 1: Get signed upload URL from backend
111→            const uploadResp = await fetchApi<UploadResponse>('/protocols/upload', {
112→                method: 'POST',
113→                body: JSON.stringify({
114→                    filename: file.name,
115→                    content_type: file.type,
116→                    file_size_bytes: file.size,
117→                }),
118→            });
119→
120→            // Step 2: Upload file directly to GCS via signed URL
121→            const putResp = await fetch(uploadResp.upload_url, {
122→                method: 'PUT',
123→                headers: { 'Content-Type': 'application/pdf' },
124→                body: file,
125→            });
126→
127→            if (!putResp.ok) {
128→                throw new Error(`Upload failed: ${putResp.status} ${putResp.statusText}`);
129→            }
130→
131→            // Step 3: Read file as base64 for quality scoring, then confirm
132→            const arrayBuffer = await file.arrayBuffer();
133→            const bytes = new Uint8Array(arrayBuffer);
134→            let binary = '';
135→            for (let i = 0; i < bytes.length; i++) {
136→                binary += String.fromCharCode(bytes[i]);
137→            }
138→            const pdfBase64 = btoa(binary);
139→
140→            await fetchApi(`/protocols/${uploadResp.protocol_id}/confirm-upload`, {
141→                method: 'POST',
142→                body: JSON.stringify({ pdf_bytes_base64: pdfBase64 }),
143→            });
144→
145→            return uploadResp;
146→        },
147→        onSuccess: () => {
148→            queryClient.invalidateQueries({ queryKey: ['protocols'] });
149→        },
150→    });
151→}
152→
