158→            fetchApi(`/protocols/${id}/confirm-upload`, {
159→                method: 'POST',
160→                body: JSON.stringify(pdfBase64 ? { pdf_bytes_base64: pdfBase64 } : {}),
161→            }),
162→        onSuccess: () => {
163→            queryClient.invalidateQueries({ queryKey: ['protocols'] });
164→        },
165→    });
166→}
