 91→function formatRelativeTime(dateStr: string): string {
 92→    const date = new Date(dateStr);
 93→    const now = new Date();
 94→    const diffMs = now.getTime() - date.getTime();
 95→    const diffSec = Math.floor(diffMs / 1000);
 96→    const diffMin = Math.floor(diffSec / 60);
 97→    const diffHr = Math.floor(diffMin / 60);
 98→    const diffDay = Math.floor(diffHr / 24);
 99→
100→    if (diffSec < 60) return 'just now';
101→    if (diffMin < 60) return `${diffMin}m ago`;
102→    if (diffHr < 24) return `${diffHr}h ago`;
103→    if (diffDay < 30) return `${diffDay}d ago`;
104→    return date.toLocaleDateString();
105→}
