  7â†’async function fetchApi<T>(endpoint: string, options?: RequestInit): Promise<T> {
  8â†’    // Get auth token from store
  9â†’    const token = useAuthStore.getState().token;
 10â†’    const headers: HeadersInit = {
 11â†’        'Content-Type': 'application/json',
 12â†’        ...(token ? { Authorization: `Bearer ${token}` } : {}),
 13â†’        ...(options?.headers || {}),
 14â†’    };
 15â†’
 16â†’    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
 17â†’        ...options,
 18â†’        headers,
 19â†’    });
 20â†’
 21â†’    if (response.status === 401) {
 22â†’        useAuthStore.getState().logout();
 23â†’        throw new Error('Session expired');
 24â†’    }
 25â†’
 26â†’    if (!response.ok) {
 27â†’        throw new Error(`API Error: ${response.status} ${response.statusText}`);
 28â†’    }
 29â†’
 30â†’    return response.json();
 31â†’}
