105â†’export function useUploadProtocol() {
106â†’    const queryClient = useQueryClient();
107â†’
108â†’    return useMutation({
109â†’        mutationFn: async ({ file }: { file: File }) => {
110â†’            // Step 1: Get signed upload URL from backend
111â†’            const uploadResp = await fetchApi<UploadResponse>('/protocols/upload', {
112â†’                method: 'POST',
113â†’                body: JSON.stringify({
114â†’                    filename: file.name,
115â†’                    content_type: file.type,
116â†’                    file_size_bytes: file.size,
117â†’                }),
118â†’            });
119â†’
120â†’            // Step 2: Upload file directly to GCS via signed URL
121â†’            const putResp = await fetch(uploadResp.upload_url, {
122â†’                method: 'PUT',
123â†’                headers: { 'Content-Type': 'application/pdf' },
124â†’                body: file,
125â†’            });
126â†’
127â†’            if (!putResp.ok) {
128â†’                throw new Error(`Upload failed: ${putResp.status} ${putResp.statusText}`);
129â†’            }
130â†’
131â†’            // Step 3: Read file as base64 for quality scoring, then confirm
132â†’            const arrayBuffer = await file.arrayBuffer();
133â†’            const bytes = new Uint8Array(arrayBuffer);
134â†’            let binary = '';
135â†’            for (let i = 0; i < bytes.length; i++) {
136â†’                binary += String.fromCharCode(bytes[i]);
137â†’            }
138â†’            const pdfBase64 = btoa(binary);
139â†’
140â†’            await fetchApi(`/protocols/${uploadResp.protocol_id}/confirm-upload`, {
141â†’                method: 'POST',
142â†’                body: JSON.stringify({ pdf_bytes_base64: pdfBase64 }),
143â†’            });
144â†’
145â†’            return uploadResp;
146â†’        },
147â†’        onSuccess: () => {
148â†’            queryClient.invalidateQueries({ queryKey: ['protocols'] });
149â†’        },
150â†’    });
