  6â†’function formatRelativeTime(dateStr: string): string {
  7â†’    const date = new Date(dateStr);
  8â†’    const now = new Date();
  9â†’    const diffMs = now.getTime() - date.getTime();
 10â†’    const diffSec = Math.floor(diffMs / 1000);
 11â†’    const diffMin = Math.floor(diffSec / 60);
 12â†’    const diffHr = Math.floor(diffMin / 60);
 13â†’    const diffDay = Math.floor(diffHr / 24);
 14â†’
 15â†’    if (diffSec < 60) return 'just now';
 16â†’    if (diffMin < 60) return `${diffMin}m ago`;
 17â†’    if (diffHr < 24) return `${diffHr}h ago`;
 18â†’    if (diffDay < 30) return `${diffDay}d ago`;
 19â†’    return date.toLocaleDateString();
 20â†’}
